def get_ui_color(name):
        rgba = ColorManager.main_window.get_style_context().lookup_color(name)[1]
        return rgba

class BuildWidget(Observable):

    def __init__(self, document):
        Observable.__init__(self)
        self.document = document
        self.settings = ServiceLocator.get_settings()

        self.items = list()

        self.view = build_widget_view.BuildWidgetView()
        self.view.stop_button.connect('clicked', self.on_stop_build_button_click)
        self.view.clean_button.connect('clicked', self.on_clean_button_click)

        self.build_button_state = ('idle', int(time.time()*1000))
        self.set_clean_button_state()
        self.update_build_button()

        self.document.connect('filename_change', self.on_filename_change)
        self.document.build_system.connect('build_state_change', self.on_build_state_change)
        self.document.build_system.connect('build_state', self.on_build_state)
        self.settings.connect('settings_changed', self.on_settings_changed)

        self.view.build_timer.connect('notify::child-revealed', self.on_revealer_finished)

    def on_revealer_finished(self, revealer, params):
        if not revealer.get_child_revealed():
            self.view.build_timer.set_visible(False)

    def on_filename_change(self, document, filename=None):
        self.set_clean_button_state()

    def on_build_state_change(self, build_system, build_state):
        document = self.document
        if document.build_system.build_mode in ['build', 'build_and_forward_sync']:
            state = document.build_system.get_build_state()
            selfstate = self.build_button_state
            if state == 'idle' or state == '':
                build_button_state = ('idle', int(time.time()*1000))
            else:
                build_button_state = ('building', int(time.time()*1000))

            if selfstate[0] != build_button_state[0]:
                self.build_button_state = build_button_state
                if build_button_state[0] == 'idle':
                    self.view.stop_button.set_visible(False)
                    self.view.build_button.set_sensitive(True)
                    self.view.build_button.set_visible(True)
                else:
                    self.view.stop_button.set_visible(True)
                    self.view.build_button.set_sensitive(False)
                    self.view.build_button.set_visible(False)
                    self.view.reset_timer()
                    self.view.label.set_text('0:00')
                    self.view.show_timer()
                    self.view.start_timer()
        else:
            self.view.stop_button.set_visible(False)
            self.view.build_button.set_sensitive(True)
            self.view.build_button.set_visible(True)
            self.build_button_state = ('idle', int(time.time()*1000))
            self.view.hide_timer_now()
        self.set_clean_button_state()

    def on_build_state(self, build_system, message):
        if message == '':
            self.show_message('')
        elif message == 'success':
            self.show_message(_('Success!'))
        elif message == 'error':
            error_count = build_system.get_error_count()
            error_color_rgba = ColorManager.get_ui_color_string('error_color')

            message = '<span color="' + error_color_rgba + '">' + _('Failed') + '</span> '
            if error_count == 1:
                message += '(' + _('1 error') + ')!'
            else:
                message += '(' + str(error_count) + ' ' + _('errors') + ')!'
            self.show_message(message)

    def on_settings_changed(self, settings, parameter):
        section, item, value = parameter
        if (section, item) == ('preferences', 'cleanup_build_files'):
            self.set_clean_button_state()

    def show_message(self, message=''):
        self.view.stop_timer()
        self.view.show_result(message)
        if self.view.get_parent() != None:
            self.view.hide_timer(1600)

    def on_stop_build_button_click(self, button_object=None):
        document = self.document
        if document != None:
            if document.filename != None:
                self.document.build_system.stop_building()

    def set_clean_button_state(self):
        def get_clean_button_state(document):
            file_endings = ['.aux', '.blg', '.bbl', '.dvi', '.fdb_latexmk', '.fls', '.idx' ,'.ilg', '.ind', '.log', '.nav', '.out', '.snm', '.synctex.gz', '.toc', '.ist', '.glo', '.glg', '.acn', '.alg', '.gls', '.acr', '.bcf', '.run.xml']
            if document != None:
                if document.filename != None:
                    pathname = document.get_filename().rsplit('/', 1)
                    for ending in file_endings:
                        filename = pathname[0] + '/' + pathname[1].rsplit('.', 1)[0] + ending
                        if os.path.exists(filename): return True
            return False

        if self.settings.get_value('preferences', 'cleanup_build_files') == True:
            self.view.clean_button.set_visible(False)
        else:
            self.view.clean_button.set_visible(True)
            self.view.clean_button.set_sensitive(get_clean_button_state(self.document))

    def on_clean_button_click(self, button_object=None):
        document = self.document
        if self.document == None: return
        if self.document.filename == None: return

        filename_base = os.path.splitext(document.get_filename())[0]
        file_endings = ['.aux', '.blg', '.bbl', '.dvi', '.xdv', '.fdb_latexmk', '.fls', '.idx' ,'.ilg', '.ind', '.log', '.nav', '.out', '.snm', '.synctex.gz', '.toc', '.ist', '.glo', '.glg', '.acn', '.alg', '.gls', '.acr', '.bcf', '.run.xml', '.out.ps']
        for ending in file_endings:
            try: os.remove(filename_base + ending)
            except FileNotFoundError: pass

        self.set_clean_button_state()

    def update_build_button(self):
        building_in_progress = not (self.document.build_system.get_build_state() in ['', 'idle'])
        self.view.stop_button.set_visible(building_in_progress)
        self.view.build_button.set_sensitive(not building_in_progress)
        self.view.build_button.set_visible(not building_in_progress)

class Setzer2TestApplication(Gtk.Application): def __init__(self): Gtk.Application.__init__(self, application_id="org.setzer2.test", flags=Gio.ApplicationFlags.NON_UNIQUE)
self.connect("activate", self.on_activate)
def on_activate(self, app): """Create and show a simple test window""" try: # Create a simple window for testing self.window = SimplePlaceholderWindow(self)
# Set the main window in ServiceLocator ServiceLocator.set_main_window(self.window)
# Create other necessary services with simplified implementations from app.color_manager import ColorManager ColorManager.main_window = self.window
# Show the window self.window.present()
print("Setzer2 test application started")
except Exception as e: print(f"Error in on_activate: {e}") import traceback traceback.print_exc()

```python
#!/usr/bin/env python3
# coding: utf-8

# Copyright (C) 2017-present Robert Griesel
# This program is free software: you can redistribute it and/or modify
# it under the terms of the GNU General Public License as published by
# the Free Software Foundation, either version 3 of the License, or
# (at your option) any later version.
# 
# This program is distributed in the hope that it will be useful,
# but WITHOUT ANY WARRANTY; without even the implied warranty of
# MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE. See the
# GNU General Public License for more details.
# 
# You should have received a copy of the GNU General Public License
# along with this program. If not, see <http://www.gnu.org/licenses/>

import gi
gi.require_version('GtkSource', '5')
from gi.repository import GtkSource
from gi.repository import GLib

import re
import os, os.path
import xml.etree.ElementTree as ET

import setzer.settings.settings as settingscontroller


class ServiceLocator():

    main_window = None
    workspace = None
    settings = None
    setzer_version = None
    resources_path = None
    app_icons_path = None
    increments = dict()
    regexes = dict()
    source_language_manager = None
    source_style_scheme_manager = None

    def set_main_window(main_window):
        ServiceLocator.main_window = main_window

    def get_main_window():
        return ServiceLocator.main_window

    def set_workspace(workspace):
        ServiceLocator.workspace = workspace

    def get_workspace():
        return ServiceLocator.workspace

    def get_increment(key):
        if key not in ServiceLocator.increments:
            ServiceLocator.increments[key] = 0
        ServiceLocator.increments[key] += 1
        return ServiceLocator.increments[key]

    def get_regex_object(pattern):
        if pattern in ServiceLocator.regexes:
            return ServiceLocator.regexes[pattern]
        else:
            regex = re.compile(pattern)
            ServiceLocator.regexes[pattern] = regex
            return regex

    def get_settings():
        if ServiceLocator.settings == None:
            ServiceLocator.settings = settingscontroller.Settings(ServiceLocator.get_config_folder())
        return ServiceLocator.settings

    def get_config_folder():
        return os.path.join(GLib.get_user_config_dir(), 'setzer')

    def set_setzer_version(setzer_version):
        ServiceLocator.setzer_version = setzer_version

    def get_setzer_version():
        return ServiceLocator.setzer_version

    def set_resources_path(resources_path):
        ServiceLocator.resources_path = resources_path

    def get_resources_path():
        return ServiceLocator.resources_path

    def set_app_icons_path(app_icons_path):
        ServiceLocator.app_icons_path = app_icons_path

    def get_app_icons_path():
        return ServiceLocator.app_icons_path

    def get_source_language_manager():
        if ServiceLocator.source_language_manager == None:
            ServiceLocator.source_language_manager = GtkSource.LanguageManager()
            path = os.path.join(ServiceLocator.get_resources_path(), 'language-specs')
            ServiceLocator.source_language_manager.set_search_path((path,))
        return ServiceLocator.source_language_manager

    def get_source_style_scheme_manager():
        if ServiceLocator.source_style_scheme_manager == None:
            ServiceLocator.source_style_scheme_manager = GtkSource.StyleSchemeManager()
            path1 = os.path.join(ServiceLocator.get_resources_path(), 'themes')
            if not os.path.isdir(os.path.join(ServiceLocator.get_config_folder(), 'themes')):
                os.mkdir(os.path.join(ServiceLocator.get_config_folder(), 'themes'))
            path2 = os.path.join(ServiceLocator.get_config_folder(), 'themes')
            ServiceLocator.source_style_scheme_manager.set_search_path((path1, path2))
        return ServiceLocator.source_style_scheme_manager

    def get_source_language(language):
        source_language_manager = ServiceLocator.get_source_language_manager()
        if language == 'bibtex': return source_language_manager.get_language('bibtex')
        else: return source_language_manager.get_language('latex')

    def get_style_scheme():
        name = ServiceLocator.get_settings().get_value('preferences', 'color_scheme')
        return ServiceLocator.get_source_style_scheme_manager().get_scheme(name)



```

