for tooltip, text, icon in buttons: button = create_button(icon, tooltip, lambda btn, t=text: self.insert_latex_command(t)) toolbar.append(button)
# Source view for LaTeX editing scrolled_window = Gtk.ScrolledWindow() scrolled_window.set_vexpand(True)
self.source_view = GtkSource.View() self.source_view.set_monospace(True) self.source_view.set_auto_indent(True) self.source_view.set_indent_width(2) self.source_view.set_insert_spaces_instead_of_tabs(True) self.source_view.set_tab_width(2) self.source_view.set_show_line_numbers(True) self.source_view.set_highlight_current_line(True)
# Create and set buffer with LaTeX sample self.buffer = GtkSource.Buffer() self.buffer.set_text(self.get_sample_document())
# Set syntax highlighting for LaTeX manager = ServiceLocator.get_source_language_manager() latex_lang = manager.get_language("latex") if latex_lang: self.buffer.set_language(latex_lang)
# Set style scheme style_scheme_manager = ServiceLocator.get_source_style_scheme_manager() style_scheme = style_scheme_manager.get_scheme("classic") if style_scheme: self.buffer.set_style_scheme(style_scheme)
self.source_view.set_buffer(self.buffer) scrolled_window.set_child(self.source_view)
# Add toolbar and editor to the box editor_box.append(toolbar) editor_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL)) editor_box.append(scrolled_window)
