def create_statusbar(self): statusbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL) statusbar.add_css_class("statusbar") statusbar.set_spacing(10) statusbar.set_margin_start(10) statusbar.set_margin_end(10) statusbar.set_margin_top(5) statusbar.set_margin_bottom(5)
