statusbar.append(status_label) statusbar.append(position_label)
return statusbar
def apply_settings(self): # Apply settings from ServiceLocator settings = ServiceLocator.get_settings()
# Font settings if settings.get_value("preferences", "use_system_font"): # Use system monospace font font_desc = Pango.FontDescription.from_string("monospace") else: # Use custom font font_string = settings.get_value("preferences", "font_string") font_desc = Pango.FontDescription.from_string(font_string)
self.source_view.override_font(font_desc)
# Editor settings self.source_view.set_show_line_numbers(settings.get_value("preferences", "show_line_numbers")) self.source_view.set_highlight_current_line(settings.get_value("preferences", "highlight_current_line")) self.source_view.set_tab_width(settings.get_value("preferences", "tab_width")) self.source_view.set_insert_spaces_instead_of_tabs(settings.get_value("preferences", "spaces_instead_of_tabs")) self.source_view.set_wrap_mode(Gtk.WrapMode.WORD if settings.get_value("preferences", "enable_line_wrapping") else Gtk.WrapMode.NONE)
def update_cursor_position(self, label): # Get the cursor position cursor_pos = self.buffer.get_property("cursor-position") iter = self.buffer.get_iter_at_offset(cursor_pos) line = iter.get_line() + 1 column = iter.get_line_offset() + 1
# Update the position label label.set_text(f"Line: {line}, Col: {column}")
def get_sample_document(self): return r"""\documentclass{article} \usepackage[utf8]{inputenc} \usepackage{graphicx} \usepackage{amsmath} \usepackage{hyperref}
\title{Sample \LaTeX{} Document} \author{Setzer User} \date{\today}
\begin{document}
\maketitle
\section{Introduction} This is a sample \LaTeX{} document created in Setzer.
\section{Mathematics} You can write equations like this: \begin{equation} E = mc^2 \end{equation}
Or inline math like $f(x) = x^2$.
\section{Lists} \begin{itemize} \item First item \item Second item \item Third item \end{itemize}
\begin{enumerate} \item First numbered item \item Second numbered item \end{enumerate}
\section{Figures} \begin{figure} \centering \caption{A sample figure} \label{fig:sample} \end{figure}
\section{Conclusion} This is the end of our sample document.
\end{document} """
# Event handlers def on_new_document(self, button): self.buffer.set_text("\\documentclass{article}\n\\begin{document}\n\n\\end{document}") print("New document created")
def on_open_document(self, button): print("Open document clicked") # Here you would implement a file chooser dialog
def on_save_document(self, button): print("Save document clicked") # Here you would implement saving functionality
def on_build_document(self, button): print("Build document clicked") # Here you would implement LaTeX compilation
def on_refresh_structure(self, button): print("Refresh structure clicked") # Here you would parse the document and update the structure tree
def on_tree_selection_changed(self, selection): model, treeiter = selection.get_selected() if treeiter is not None: print(f"Selected: {model[treeiter][0]}") # Here you would typically jump to the corresponding position in the document
def insert_latex_command(self, command): self.buffer.begin_user_action() self.buffer.insert_at_cursor(command) self.buffer.end_user_action() print(f"Inserted: {command}")
class SetzerApp(Gtk.Application): def __init__(self): Gtk.Application.__init__(self, application_id="org.setzer.editor", flags=Gio.ApplicationFlags.NON_UNIQUE)
self.connect("activate", self.on_activate)
def on_activate(self, app): try: self.window = SetzerWindow(self) ServiceLocator.set_main_window(self.window)
# Add application actions self.create_actions()
self.window.present() print("Setzer application started successfully!")
except Exception as e: print(f"Error in activation: {e}") import traceback traceback.print_exc()
def create_actions(self): # Add application actions for menu items actions = [ ("preferences", self.on_preferences), ("shortcuts", self.on_shortcuts), ("about", self.on_about) ]
for name, callback in actions: action = Gio.SimpleAction.new(name, None) action.connect("activate", callback) self.add_action(action)
def on_preferences(self, action, param): print("Preferences menu item clicked") # Here you would open a preferences dialog
def on_shortcuts(self, action, param): print("Keyboard shortcuts menu item clicked") # Here you would show a shortcuts window
def on_about(self, action, param): print("About menu item clicked") # Here you would display an about dialog
def main(): try: print(f"Running as PID: {os.getpid()}") app = SetzerApp() exit_status = app.run(sys.argv) sys.exit(exit_status) except Exception as e: print(f"Error in main: {e}") import traceback traceback.print_exc() sys.exit(1)
if __name__ == "__main__": main() ```
This enhanced UI includes:
1. **Editor Area** with a GtkSource.View configured for LaTeX editing and syntax highlighting 2. **Document Structure Sidebar** with a treeview showing a sample document structure 3. **LaTeX Command Toolbar** with buttons for common LaTeX formatting and structures 4. **Status Bar** showing cursor position 5. **Header Bar** with document and build actions 6. **Settings Menu** with preferences, shortcuts, and about options 7. **Sample LaTeX Document** pre-loaded in the editor
The UI is styled with CSS to create a clean, modern look. Functions are only implemented to print messages when clicked, but you could expand them to add real functionality as needed.

