@staticmethod def get_regex_object(pattern): import re if pattern in ServiceLocator.regexes: return ServiceLocator.regexes[pattern] else: regex = re.compile(pattern) ServiceLocator.regexes[pattern] = regex return regex
@staticmethod def get_settings(): if ServiceLocator.settings is None: # Use our own Settings class from gi.repository import GLib config_folder = ServiceLocator.get_config_folder() ServiceLocator.settings = Settings(config_folder) return ServiceLocator.settings
@staticmethod def get_config_folder(): from gi.repository import GLib return os.path.join(GLib.get_user_config_dir(), 'setzer')
