@staticmethod def get_app_icons_path(): return ServiceLocator.app_icons_path
@staticmethod def get_source_language_manager(): if ServiceLocator.source_language_manager is None: from gi.repository import GtkSource ServiceLocator.source_language_manager = GtkSource.LanguageManager() path = os.path.join(ServiceLocator.get_resources_path(), 'language-specs') ServiceLocator.source_language_manager.set_search_path((path,)) return ServiceLocator.source_language_manager
@staticmethod def get_source_style_scheme_manager(): if ServiceLocator.source_style_scheme_manager is None: from gi.repository import GtkSource import os ServiceLocator.source_style_scheme_manager = GtkSource.StyleSchemeManager() path1 = os.path.join(ServiceLocator.get_resources_path(), 'themes') config_folder = ServiceLocator.get_config_folder() themes_folder = os.path.join(config_folder, 'themes') if not os.path.isdir(themes_folder): os.makedirs(themes_folder, exist_ok=True) path2 = themes_folder ServiceLocator.source_style_scheme_manager.set_search_path((path1, path2)) return ServiceLocator.source_style_scheme_manager
@staticmethod def get_source_language(language): source_language_manager = ServiceLocator.get_source_language_manager() if language == 'bibtex': return source_language_manager.get_language('bibtex') else: return source_language_manager.get_language('latex')
@staticmethod def get_style_scheme(): name = ServiceLocator.get_settings().get_value('preferences', 'color_scheme') return ServiceLocator.get_source_style_scheme_manager().get_scheme(name)
# Setup GTK gi.require_version('Gtk', '4.0') gi.require_version('GtkSource', '5') gi.require_version('Adw', '1') from gi.repository import Gtk, GLib, Gio, Adw, GtkSource, Gdk, Pango
# Set application resources path resources_path = os.path.join(project_dir, 'resources') if not os.path.exists(resources_path): os.makedirs(resources_path, exist_ok=True) os.makedirs(os.path.join(resources_path, 'icons'), exist_ok=True) os.makedirs(os.path.join(resources_path, 'language-specs'), exist_ok=True) os.makedirs(os.path.join(resources_path, 'themes'), exist_ok=True)
app_icons_path = os.path.join(resources_path, 'icons') ServiceLocator.set_resources_path(resources_path) ServiceLocator.set_app_icons_path(app_icons_path) ServiceLocator.set_setzer_version('2.0.0')
# Create a toolbar button with icon def create_button(icon_name, tooltip_text, clicked_callback=None): button = Gtk.Button() button.set_tooltip_text(tooltip_text)
icon = Gtk.Image.new_from_icon_name(icon_name) button.set_child(icon)
if clicked_callback: button.connect("clicked", clicked_callback)
return button
# Create a menu button def create_menu_button(icon_name, tooltip_text, menu_model): button = Gtk.MenuButton() button.set_tooltip_text(tooltip_text)
