# Enhanced window for testing Setzer UI class SetzerWindow(Adw.ApplicationWindow): def __init__(self, app): Adw.ApplicationWindow.__init__(self, application=app) self.set_default_size(1100, 700) self.set_title("Setzer - LaTeX Editor")
self.create_css() self.create_widgets() self.apply_settings()
def create_css(self): # Add CSS for styling css_provider = Gtk.CssProvider() css = ''' .sidebar { background-color: #f5f5f5; border-right: 1px solid #ddd; }
.editor { background-color: #ffffff; }
.toolbar { background-color: #f8f9fa; border-bottom: 1px solid #ddd; }
.statusbar { background-color: #f8f9fa; border-top: 1px solid #ddd; font-size: 0.9em; }
.highlight { background-color: #e2eefe; } ''' css_provider.load_from_data(css.encode()) Gtk.StyleContext.add_provider_for_display( Gdk.Display.get_default(), css_provider, Gtk.STYLE_PROVIDER_PRIORITY_APPLICATION )
def create_widgets(self): # Main layout container self.main_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)
# Create header bar with toolbar self.header = self.create_header_bar()
# Create the main paned container (sidebar and editor area) self.main_paned = Gtk.Paned(orientation=Gtk.Orientation.HORIZONTAL)
# Create sidebar self.sidebar = self.create_sidebar() self.sidebar.set_size_request(250, -1)
# Create editor area self.editor_box = self.create_editor_area()
# Add sidebar and editor to paned container self.main_paned.set_start_child(self.sidebar) self.main_paned.set_end_child(self.editor_box) self.main_paned.set_resize_start_child(False)
# Create status bar self.statusbar = self.create_statusbar()
# Add all components to main box self.main_box.append(self.header) self.main_box.append(self.main_paned) self.main_box.append(self.statusbar)
# Set content self.set_content(self.main_box)
def create_header_bar(self): header = Adw.HeaderBar() header.add_css_class("toolbar")
# Document actions new_button = create_button("document-new-symbolic", "New Document", self.on_new_document) header.pack_start(new_button)
open_button = create_button("document-open-symbolic", "Open Document", self.on_open_document) header.pack_start(open_button)
save_button = create_button("document-save-symbolic", "Save Document", self.on_save_document) header.pack_start(save_button)
# Build actions build_button = create_button("system-run-symbolic", "Build Document", self.on_build_document) header.pack_end(build_button)
# Settings menu menu_model = Gio.Menu() menu_model.append("Preferences", "app.preferences") menu_model.append("Keyboard Shortcuts", "app.shortcuts") menu_model.append("About Setzer", "app.about")
menu_button = create_menu_button("open-menu-symbolic", "Settings", menu_model) header.pack_end(menu_button)
return header
def create_sidebar(self): sidebar_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL) sidebar_box.add_css_class("sidebar")
# Sidebar header sidebar_header = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL) sidebar_header.set_margin_top(10) sidebar_header.set_margin_bottom(10) sidebar_header.set_margin_start(10) sidebar_header.set_margin_end(10)
sidebar_label = Gtk.Label(label="Document Structure") sidebar_label.set_halign(Gtk.Align.START) sidebar_label.set_hexpand(True)
refresh_button = create_button("view-refresh-symbolic", "Refresh Structure", self.on_refresh_structure) refresh_button.set_valign(Gtk.Align.CENTER)
sidebar_header.append(sidebar_label) sidebar_header.append(refresh_button)
# Document structure treeview scrolled_window = Gtk.ScrolledWindow() scrolled_window.set_vexpand(True)
# Create a simple tree store model self.tree_store = Gtk.TreeStore(str, str) # Text, icon name
# Add some example items section1 = self.tree_store.append(None, ["Introduction", "text-x-generic"]) self.tree_store.append(section1, ["Background", "text-x-generic"]) self.tree_store.append(section1, ["Motivation", "text-x-generic"])
section2 = self.tree_store.append(None, ["Methods", "text-x-generic"]) self.tree_store.append(section2, ["Experiment Setup", "text-x-generic"]) self.tree_store.append(section2, ["Data Collection", "text-x-generic"])
section3 = self.tree_store.append(None, ["Results", "text-x-generic"]) self.tree_store.append(None, ["Discussion", "text-x-generic"]) self.tree_store.append(None, ["Conclusion", "text-x-generic"]) self.tree_store.append(None, ["References", "text-x-generic"])
# Create the treeview self.tree_view = Gtk.TreeView(model=self.tree_store) self.tree_view.set_headers_visible(False)
# Create a column for the tree view renderer_icon = Gtk.CellRendererPixbuf() renderer_text = Gtk.CellRendererText()
column = Gtk.TreeViewColumn("Structure") column.pack_start(renderer_icon, False) column.pack_start(renderer_text, True)
column.add_attribute(renderer_icon, "icon-name", 1) column.add_attribute(renderer_text, "text", 0)
self.tree_view.append_column(column)
# Connect to selection change select = self.tree_view.get_selection() select.connect("changed", self.on_tree_selection_changed)
scrolled_window.set_child(self.tree_view)
# Put everything in the sidebar sidebar_box.append(sidebar_header) sidebar_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL)) sidebar_box.append(scrolled_window)
return sidebar_box
def create_editor_area(self): editor_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL) editor_box.add_css_class("editor")
# Toolbar for common LaTeX commands toolbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL) toolbar.add_css_class("toolbar") toolbar.set_margin_start(5) toolbar.set_margin_end(5) toolbar.set_margin_top(5) toolbar.set_margin_bottom(5) toolbar.set_spacing(5)
# Add LaTeX command buttons buttons = [ ("Bold", "\\textbf{}", "format-text-bold-symbolic"), ("Italic", "\\textit{}", "format-text-italic-symbolic"), ("Underline", "\\underline{}", "format-text-underline-symbolic"), ("Math Mode", "$", "accessories-calculator-symbolic"), ("Enumerate", "\\begin{enumerate}\n\\item \n\\end{enumerate}", "view-list-symbolic"), ("Itemize", "\\begin{itemize}\n\\item \n\\end{itemize}", "view-list-bullet-symbolic"), ("Figure", "\\begin{figure}\n\\centering\n\\includegraphics{}\n\\caption{}\n\\label{fig:}\n\\end{figure}", "insert-image-symbolic"), ("Table", "\\begin{table}\n\\centering\n\\caption{}\n\\label{tab:}\n\\begin{tabular}{}\n\\end{tabular}\n\\end{table}", "x-office-spreadsheet-symbolic"), ]
for tooltip, text, icon in buttons: button = create_button(icon, tooltip, lambda btn, t=text: self.insert_latex_command(t)) toolbar.append(button)
# Source view for LaTeX editing scrolled_window = Gtk.ScrolledWindow() scrolled_window.set_vexpand(True)
self.source_view = GtkSource.View() self.source_view.set_monospace(True) self.source_view.set_auto_indent(True) self.source_view.set_indent_width(2) self.source_view.set_insert_spaces_instead_of_tabs(True) self.source_view.set_tab_width(2) self.source_view.set_show_line_numbers(True) self.source_view.set_highlight_current_line(True)
# Create and set buffer with LaTeX sample self.buffer = GtkSource.Buffer() self.buffer.set_text(self.get_sample_document())
# Set syntax highlighting for LaTeX manager = ServiceLocator.get_source_language_manager() latex_lang = manager.get_language("latex") if latex_lang: self.buffer.set_language(latex_lang)
# Set style scheme style_scheme_manager = ServiceLocator.get_source_style_scheme_manager() style_scheme = style_scheme_manager.get_scheme("classic") if style_scheme: self.buffer.set_style_scheme(style_scheme)
self.source_view.set_buffer(self.buffer) scrolled_window.set_child(self.source_view)
# Add toolbar and editor to the box editor_box.append(toolbar) editor_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL)) editor_box.append(scrolled_window)
return editor_box
def create_statusbar(self): statusbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL) statusbar.add_css_class("statusbar") statusbar.set_spacing(10) statusbar.set_margin_start(10) statusbar.set_margin_end(10) statusbar.set_margin_top(5) statusbar.set_margin_bottom(5)
status_label = Gtk.Label(label="Ready") status_label.set_halign(Gtk.Align.START) status_label.set_hexpand(True)
position_label = Gtk.Label(label="Line: 1, Col: 1") position_label.set_halign(Gtk.Align.END)
# Update position when cursor moves self.buffer.connect("notify::cursor-position", lambda buf, param: self.update_cursor_position(position_label))
statusbar.append(status_label) statusbar.append(position_label)
return statusbar
def apply_settings(self): # Apply settings from ServiceLocator settings = ServiceLocator.get_settings()
# Font settings if settings.get_value("preferences", "use_system_font"): # Use system monospace font font_desc = Pango.FontDescription.from_string("monospace") else: # Use custom font font_string = settings.get_value("preferences", "font_string") font_desc = Pango.FontDescription.from_string(font_string)
self.source_view.override_font(font_desc)
# Editor settings self.source_view.set_show_line_numbers(settings.get_value("preferences", "show_line_numbers")) self.source_view.set_highlight_current_line(settings.get_value("preferences", "highlight_current_line")) self.source_view.set_tab_width(settings.get_value("preferences", "tab_width")) self.source_view.set_insert_spaces_instead_of_tabs(settings.get_value("preferences", "spaces_instead_of_tabs")) self.source_view.set_wrap_mode(Gtk.WrapMode.WORD if settings.get_value("preferences", "enable_line_wrapping") else Gtk.WrapMode.NONE)
def update_cursor_position(self, label): # Get the cursor position cursor_pos = self.buffer.get_property("cursor-position") iter = self.buffer.get_iter_at_offset(cursor_pos) line = iter.get_line() + 1 column = iter.get_line_offset() + 1
# Update the position label label.set_text(f"Line: {line}, Col: {column}")
def get_sample_document(self): return r"""\documentclass{article} \usepackage[utf8]{inputenc} \usepackage{graphicx} \usepackage{amsmath} \usepackage{hyperref}
\title{Sample \LaTeX{} Document} \author{Setzer User} \date{\today}
\begin{document}
\maketitle
\section{Introduction} This is a sample \LaTeX{} document created in Setzer.
\section{Mathematics} You can write equations like this: \begin{equation} E = mc^2 \end{equation}
Or inline math like $f(x) = x^2$.
\section{Lists} \begin{itemize} \item First item \item Second item \item Third item \end{itemize}
\begin{enumerate} \item First numbered item \item Second numbered item \end{enumerate}
\section{Figures} \begin{figure} \centering \caption{A sample figure} \label{fig:sample} \end{figure}
\section{Conclusion} This is the end of our sample document.
\end{document} """
# Event handlers def on_new_document(self, button): self.buffer.set_text("\\documentclass{article}\n\\begin{document}\n\n\\end{document}") print("New document created")
def on_open_document(self, button): print("Open document clicked") # Here you would implement a file chooser dialog
def on_save_document(self, button): print("Save document clicked") # Here you would implement saving functionality
def on_build_document(self, button): print("Build document clicked") # Here you would implement LaTeX compilation
def on_refresh_structure(self, button): print("Refresh structure clicked") # Here you would parse the document and update the structure tree
def on_tree_selection_changed(self, selection): model, treeiter = selection.get_selected() if treeiter is not None: print(f"Selected: {model[treeiter][0]}") # Here you would typically jump to the corresponding position in the document
def insert_latex_command(self, command): self.buffer.begin_user_action() self.buffer.insert_at_cursor(command) self.buffer.end_user_action() print(f"Inserted: {command}")
class SetzerApp(Gtk.Application): def __init__(self): Gtk.Application.__init__(self, application_id="org.setzer.editor", flags=Gio.ApplicationFlags.NON_UNIQUE)
self.connect("activate", self.on_activate)
def on_activate(self, app): try: self.window = SetzerWindow(self) ServiceLocator.set_main_window(self.window)
# Add application actions self.create_actions()
self.window.present() print("Setzer application started successfully!")
except Exception as e: print(f"Error in activation: {e}") import traceback traceback.print_exc()
def create_actions(self): # Add application actions for menu items actions = [ ("preferences", self.on_preferences), ("shortcuts", self.on_shortcuts), ("about", self.on_about) ]
for name, callback in actions: action = Gio.SimpleAction.new(name, None) action.connect("activate", callback) self.add_action(action)
def on_preferences(self, action, param): print("Preferences menu item clicked") # Here you would open a preferences dialog
def on_shortcuts(self, action, param): print("Keyboard shortcuts menu item clicked") # Here you would show a shortcuts window
def on_about(self, action, param): print("About menu item clicked") # Here you would display an about dialog
def main(): try: print(f"Running as PID: {os.getpid()}") app = SetzerApp() exit_status = app.run(sys.argv) sys.exit(exit_status) except Exception as e: print(f"Error in main: {e}") import traceback traceback.print_exc() sys.exit(1)
if __name__ == "__main__": main() ```
This enhanced UI includes:
1. **Editor Area** with a GtkSource.View configured for LaTeX editing and syntax highlighting 2. **Document Structure Sidebar** with a treeview showing a sample document structure 3. **LaTeX Command Toolbar** with buttons for common LaTeX formatting and structures 4. **Status Bar** showing cursor position 5. **Header Bar** with document and build actions 6. **Settings Menu** with preferences, shortcuts, and about options 7. **Sample LaTeX Document** pre-loaded in the editor
The UI is styled with CSS to create a clean, modern look. Functions are only implemented to print messages when clicked, but you could expand them to add real functionality as needed.

```python #!/usr/bin/env python3 # -*- coding: utf-8 -*-
import sys import os import gi import pickle
# Setup paths project_dir = os.path.dirname(os.path.abspath(__file__)) sys.path.insert(0, project_dir)
# Create translation function def _(text): return text
# Make it globally available import builtins builtins._ = _
# Create Observable class that's used by Settings class Observable: def __init__(self): self.observers = [] self.change_codes = []
def add_observer(self, observer): if observer not in self.observers: self.observers.append(observer)
def remove_observer(self, observer): self.observers.remove(observer)
def add_change_code(self, change_code, parameter=None): self.change_codes.append(change_code) for observer in self.observers: observer.change_notification(change_code, parameter)
def get_change_code(self, change_code): if change_code in self.change_codes: return True return False
def remove_change_code(self, change_code): self.change_codes.remove(change_code)
# Define Settings class to be used directly class Settings(Observable): def __init__(self, pathname): Observable.__init__(self) self.pathname = pathname self.data = {} self.defaults = {} self.set_defaults()
if not self.unpickle(): self.data = self.defaults.copy() self.pickle()
def set_defaults(self): self.defaults = { 'preferences': { 'cleanup_build_files': True, 'color_scheme': 'classic', 'use_system_font': True, 'font_string': 'Monospace 11', 'show_line_numbers': True, 'enable_code_folding': True, 'enable_line_wrapping': True, 'tab_width': 4, 'highlight_current_line': True, 'spaces_instead_of_tabs': True } }
def get_value(self, section, item): try: return self.data[section][item] except KeyError: try: value = self.defaults[section][item] self.set_value(section, item, value) return value except KeyError: return None
def set_value(self, section, item, value): if section not in self.data: self.data[section] = {} self.data[section][item] = value self.add_change_code('settings_changed', (section, item, value))
def unpickle(self): if not os.path.isdir(self.pathname): os.makedirs(self.pathname, exist_ok=True)
try: with open(os.path.join(self.pathname, 'settings.pickle'), 'rb') as filehandle: self.data = pickle.load(filehandle) return True except (IOError, EOFError): return False
def pickle(self): try: with open(os.path.join(self.pathname, 'settings.pickle'), 'wb') as filehandle: pickle.dump(self.data, filehandle) return True except IOError: return False
# Simple color manager for UI colors class ColorManager: main_window = None
