if not os.path.exists(resources_path):
    os.makedirs(resources_path, exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'icons'), exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'language-specs'), exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'themes'), exist_ok=True)

app_icons_path = os.path.join(resources_path, 'icons')
ServiceLocator.set_resources_path(resources_path)
ServiceLocator.set_app_icons_path(app_icons_path)
ServiceLocator.set_setzer_version('2.0.0')

# Create a toolbar button with icon
def create_button(icon_name, tooltip_text, clicked_callback=None):
    button = Gtk.Button()
    button.set_tooltip_text(tooltip_text)

    icon = Gtk.Image.new_from_icon_name(icon_name)
    button.set_child(icon)

    if clicked_callback:
        button.connect("clicked", clicked_callback)

    return button

# Create a menu button
def create_menu_button(icon_name, tooltip_text, menu_model):
    button = Gtk.MenuButton()
    button.set_tooltip_text(tooltip_text)

    icon = Gtk.Image.new_from_icon_name(icon_name)
    button.set_child(icon)
    button.set_menu_model(menu_model)

    return button

# Tree item for the document structure sidebar
class DocStructureItem:
    def __init__(self, title, level=0):
        self.title = title
        self.level = level
        self.expanded = False
        self.children = []

    def add_child(self, item):
        self.children.append(item)
        return item

# Enhanced window for testing Setzer UI
class SetzerWindow(Adw.ApplicationWindow):
    def __init__(self, app):
        Adw.ApplicationWindow.__init__(self, application=app)
        self.set_default_size(1100, 700)
        self.set_title("Setzer - LaTeX Editor")

        self.create_css()
        self.create_widgets()
        self.apply_settings()

    def create_css(self):
        # Add CSS for styling
        css_provider = Gtk.CssProvider()
        css = '''
        .sidebar {
            background-color: #f5f5f5;
            border-right: 1px solid #ddd;
        }

        .editor {
            background-color: #ffffff;
        }

        .toolbar {
            background-color: #f8f9fa;
            border-bottom: 1px solid #ddd;
        }

        .statusbar {
            background-color: #f8f9fa;
            border-top: 1px solid #ddd;
            font-size: 0.9em;
        }

        .highlight {
            background-color: #e2eefe;
        }

        .document-item {
            padding: 4px 0;
            margin-left: 12px;
        }
        '''
        css_provider.load_from_data(css.encode())
        Gtk.StyleContext.add_provider_for_display(
            Gdk.Display.get_default(),
            css_provider,
            Gtk.STYLE_PROVIDER_PRIORITY_APPLICATION
        )

    def create_widgets(self):
        # Main layout container
        self.main_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)

        # Create header bar with toolbar
        self.header = self.create_header_bar()

        # Create the main paned container (sidebar and editor area)
        self.main_paned = Gtk.Paned(orientation=Gtk.Orientation.HORIZONTAL)

        # Create sidebar
        self.sidebar = self.create_sidebar()
        self.sidebar.set_size_request(250, -1)

        # Create editor area
        self.editor_box = self.create_editor_area()

        # Add sidebar and editor to paned container
        self.main_paned.set_start_child(self.sidebar)
        self.main_paned.set_end_child(self.editor_box)
        self.main_paned.set_resize_start_child(False)

        # Create status bar
        self.statusbar = self.create_statusbar()

        # Add all components to main box
        self.main_box.append(self.header)
        self.main_box.append(self.main_paned)
        self.main_box.append(self.statusbar)

        # Set content
        self.set_content(self.main_box)

    def create_header_bar(self):
        header = Adw.HeaderBar()
        header.add_css_class("toolbar")

        # Document actions
        new_button = create_button("document-new-symbolic", "New Document", self.on_new_document)
        header.pack_start(new_button)

        open_button = create_button("document-open-symbolic", "Open Document", self.on_open_document)
        header.pack_start(open_button)

        save_button = create_button("document-save-symbolic", "Save Document", self.on_save_document)
        header.pack_start(save_button)

        # Build actions
        build_button = create_button("system-run-symbolic", "Build Document", self.on_build_document)
        header.pack_end(build_button)

        # Settings menu
        menu_model = Gio.Menu()
        menu_model.append("Preferences", "app.preferences")
        menu_model.append("Keyboard Shortcuts", "app.shortcuts")
        menu_model.append("About Setzer", "app.about")

        menu_button = create_menu_button("open-menu-symbolic", "Settings", menu_model)
        header.pack_end(menu_button)

        return header

    def create_sidebar(self):
        sidebar_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)
        sidebar_box.add_css_class("sidebar")

        # Sidebar header
        sidebar_header = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        sidebar_header.set_margin_top(10)
        sidebar_header.set_margin_bottom(10)
        sidebar_header.set_margin_start(10)
        sidebar_header.set_margin_end(10)

        sidebar_label = Gtk.Label(label="Document Structure")
        sidebar_label.set_halign(Gtk.Align.START)
        sidebar_label.set_hexpand(True)

        refresh_button = create_button("view-refresh-symbolic", "Refresh Structure", self.on_refresh_structure)
        refresh_button.set_valign(Gtk.Align.CENTER)

        sidebar_header.append(sidebar_label)
        sidebar_header.append(refresh_button)

        # Document structure list using GTK4 ListBox instead of deprecated TreeView
        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_vexpand(True)

        # Create a ListBox for document structure
        self.structure_list = Gtk.ListBox()
        self.structure_list.set_selection_mode(Gtk.SelectionMode.SINGLE)
        self.structure_list.set_activate_on_single_click(True)
        self.structure_list.connect("row-activated", self.on_structure_item_activated)

        # Populate structure
        self.populate_document_structure()

        scrolled_window.set_child(self.structure_list)

        # Put everything in the sidebar
        sidebar_box.append(sidebar_header)
        sidebar_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL))
        sidebar_box.append(scrolled_window)

        return sidebar_box

    def populate_document_structure(self):
        # Sample document structure
        structure = [
            {"title": "Introduction", "children": [
                {"title": "Background", "children": []},
                {"title": "Motivation", "children": []}
            ]},
            {"title": "Methods", "children": [
                {"title": "Experiment Setup", "children": []},
                {"title": "Data Collection", "children": []}
            ]},
            {"title": "Results", "children": []},
            {"title": "Discussion", "children": []},
            {"title": "Conclusion", "children": []},
            {"title": "References", "children": []}
        ]

        # Clear the list
        while True:
            row = self.structure_list.get_row_at_index(0)
            if row is None:
                break
            self.structure_list.remove(row)

        # Add structure items to the list
        for item in structure:
            self.add_structure_item(item, 0)

    def add_structure_item(self, item, level):
        # Create a row for the item
        row = Gtk.ListBoxRow()

        # Create the content
        box = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        box.set_margin_start(level * 16)  # Indent based on level
        box.set_margin_top(2)
        box.set_margin_bottom(2)

        # Add icon
        icon = Gtk.Image.new_from_icon_name("text-x-generic")
        icon.set_margin_end(8)
        box.append(icon)

        # Add label
        label = Gtk.Label(label=item["title"])
        label.set_halign(Gtk.Align.START)
        label.set_hexpand(True)
        box.append(label)

        row.set_child(box)
        self.structure_list.append(row)

        # Add children
        for child in item["children"]:
            self.add_structure_item(child, level + 1)

    def create_editor_area(self):
        editor_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)
        editor_box.add_css_class("editor")

        # Toolbar for common LaTeX commands
        toolbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        toolbar.add_css_class("toolbar")
        toolbar.set_margin_start(5)
        toolbar.set_margin_end(5)
        toolbar.set_margin_top(5)
        toolbar.set_margin_bottom(5)
        toolbar.set_spacing(5)

        # Add LaTeX command buttons
        buttons = [
            ("Bold", "\\textbf{}", "format-text-bold-symbolic"),
            ("Italic", "\\textit{}", "format-text-italic-symbolic"),
            ("Underline", "\\underline{}", "format-text-underline-symbolic"),
            ("Math Mode", "$", "accessories-calculator-symbolic"),
            ("Enumerate", "\\begin{enumerate}\n\\item \n\\end{enumerate}", "view-list-symbolic"),
            ("Itemize", "\\begin{itemize}\n\\item \n\\end{itemize}", "view-list-bullet-symbolic"),
            ("Figure", "\\begin{figure}\n\\centering\n\\includegraphics{}\n\\caption{}\n\\label{fig:}\n\\end{figure}", "insert-image-symbolic"),
            ("Table", "\\begin{table}\n\\centering\n\\caption{}\n\\label{tab:}\n\\begin{tabular}{}\n\\end{tabular}\n\\end{table}", "x-office-spreadsheet-symbolic"),
        ]

        for tooltip, text, icon in buttons:
            button = create_button(icon, tooltip, lambda btn, t=text: self.insert_latex_command(t))
            toolbar.append(button)

        # Source view for LaTeX editing
        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_vexpand(True)

        self.source_view = GtkSource.View()
        self.source_view.set_monospace(True)
        self.source_view.set_auto_indent(True)
        self.source_view.set_indent_width(2)
        self.source_view.set_insert_spaces_instead_of_tabs(True)
        self.source_view.set_tab_width(2)
        self.source_view.set_show_line_numbers(True)
        self.source_view.set_highlight_current_line(True)

        # Set the font using CSS for GTK4
        css_provider = Gtk.CssProvider()
        css = '''
        textview {
            font-family: monospace;
            font-size: 11pt;
        }
        '''
        css_provider.load_from_data(css.encode())
        self.source_view.get_style_context().add_provider(
            css_provider,
            Gtk.STYLE_PROVIDER_PRIORITY_APPLICATION
        )

        # Create and set buffer with LaTeX sample
        self.buffer = GtkSource.Buffer()
        self.buffer.set_text(self.get_sample_document())

        # Set syntax highlighting for LaTeX
        manager = ServiceLocator.get_source_language_manager()
        latex_lang = manager.get_language("latex")
        if latex_lang:
            self.buffer.set_language(latex_lang)

        # Set style scheme
        style_scheme_manager = ServiceLocator.get_source_style_scheme_manager()
        style_scheme = style_scheme_manager.get_scheme("classic")
        if style_scheme:
            self.buffer.set_style_scheme(style_scheme)

        self.source_view.set_buffer(self.buffer)
        scrolled_window.set_child(self.source_view)

        # Add toolbar and editor to the box
        editor_box.append(toolbar)
        editor_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL))
        editor_box.append(scrolled_window)

        return editor_box

    def create_statusbar(self):
        statusbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        statusbar.add_css_class("statusbar")
        statusbar.set_spacing(10)
        statusbar.set_margin_start(10)
        statusbar.set_margin_end(10)
        statusbar.set_margin_top(5)
        statusbar.set_margin_bottom(5)

        status_label = Gtk.Label(label="Ready")
        status_label.set_halign(Gtk.Align.START)
        status_label.set_hexpand(True)

        position_label = Gtk.Label(label="Line: 1, Col: 1")
        position_label.set_halign(Gtk.Align.END)

        # Update position when cursor moves
        self.buffer.connect("notify::cursor-position", lambda buf, param: self.update_cursor_position(position_label))

        statusbar.append(status_label)
        statusbar.append(position_label)

        return statusbar

    def apply_settings(self):
        # Apply settings from ServiceLocator
        settings = ServiceLocator.get_settings()

        # Editor settings - we no longer use override_font since it's deprecated
        # Font setting is handled via CSS above
        self.source_view.set_show_line_numbers(settings.get_value("preferences", "show_line_numbers"))
        self.source_view.set_highlight_current_line(settings.get_value("preferences", "highlight_current_line"))
        self.source_view.set_tab_width(settings.get_value("preferences", "tab_width"))
        self.source_view.set_insert_spaces_instead_of_tabs(settings.get_value("preferences", "spaces_instead_of_tabs"))
        self.source_view.set_wrap_mode(Gtk.WrapMode.WORD if settings.get_value("preferences", "enable_line_wrapping") else Gtk.WrapMode.NONE)

    def update_cursor_position(self, label):
        # Get the cursor position
        cursor_pos = self.buffer.get_property("cursor-position")
        iter = self.buffer.get_iter_at_offset(cursor_pos)
        line = iter.get_line() + 1
        column = iter.get_line_offset() + 1

        # Update the position label
        label.set_text(f"Line: {line}, Col: {column}")

    def get_sample_document(self):
        return r"""\documentclass{article}
\usepackage[utf8]{inputenc}
\usepackage{graphicx}
\usepackage{amsmath}
\usepackage{hyperref}

\title{Sample \LaTeX{} Document}
\author{Setzer User}
\date{\today}

\begin{document}

\maketitle

\section{Introduction}
This is a sample \LaTeX{} document created in Setzer.

\section{Mathematics}
You can write equations like this:
\begin{equation}
    E = mc^2
\end{equation}

Or inline math like $f(x) = x^2$.

\section{Lists}
\begin{itemize}
    \item First item
    \item Second item
    \item Third item
\end{itemize}

\begin{enumerate}
    \item First numbered item
    \item Second numbered item
\end{enumerate}

\section{Figures}
\begin{figure}
    \centering
    \caption{A sample figure}
    \label{fig:sample}
\end{figure}

\section{Conclusion}
This is the end of our sample document.

\end{document}
"""

    # Event handlers
    def on_new_document(self, button):
        self.buffer.set_text("\\documentclass{article}\n\\begin{document}\n\n\\end{document}")
        print("New document created")

    def on_open_document(self, button):
        print("Open document clicked")
        # Here you would implement a file chooser dialog

    def on_save_document(self, button):
        print("Save document clicked")
        # Here you would implement saving functionality

    def on_build_document(self, button):
        print("Build document clicked")
        # Here you would implement LaTeX compilation

    def on_refresh_structure(self, button):
        print("Refresh structure clicked")
        self.populate_document_structure()

    def on_structure_item_activated(self, list_box, row):
        index = row.get_index()
        print(f"Selected item at index: {index}")
        # Here you would jump to the corresponding position in the document

    def insert_latex_command(self, command):
        self.buffer.begin_user_action()
        self.buffer.insert_at_cursor(command)
        self.buffer.end_user_action()
        print(f"Inserted: {command}")

class SetzerApp(Gtk.Application):
    def __init__(self):
        Gtk.Application.__init__(self, 
                               application_id="org.setzer.editor",
                               flags=Gio.ApplicationFlags.NON_UNIQUE)

        self.connect("activate", self.on_activate)

    def on_activate(self, app):
        try:
            self.window = SetzerWindow(self)
            ServiceLocator.set_main_window(self.window)

            # Add application actions
            self.create_actions()

            self.window.present()
            print("Setzer application started successfully!")

        except Exception as e:
            print(f"Error in activation: {e}")
            import traceback
            traceback.print_exc()

    def create_actions(self):
        # Add application actions for menu items
        actions = [
            ("preferences", self.on_preferences),
            ("shortcuts", self.on_shortcuts),
            ("about", self.on_about)
        ]

        for name, callback in actions:
            action = Gio.SimpleAction.new(name, None)
            action.connect("activate", callback)
            self.add_action(action)

    def on_preferences(self, action, param):
        print("Preferences menu item clicked")
        # Here you would open a preferences dialog

    def on_shortcuts(self, action, param):
        print("Keyboard shortcuts menu item clicked")
        # Here you would show a shortcuts window

    def on_about(self, action, param):
        print("About menu item clicked")
        # Here you would display an about dialog

def main():
    try:
        print(f"Running as PID: {os.getpid()}")
        app = SetzerApp()
        exit_status = app.run(sys.argv)
        sys.exit(exit_status)
    except Exception as e:
        print(f"Error in main: {e}")
        import traceback
        traceback.print_exc()
        sys.exit(1)

if __name__ == "__main__":
    main()
```

Key changes:

1. **Replaced TreeView with ListBox** - Instead of using the deprecated GTK.TreeView and its related methods, we're now using a GTK4-compatible Gtk.ListBox for the document structure sidebar.

2. **Fixed font setting** - Removed the deprecated `override_font` method and replaced it with CSS styling for the text view.

3. **Restructured document structure handling** - Created a cleaner structure for the document elements that works with GTK4.

4. **Updated event handling** - Modified how selection in the document structure works.

These changes should eliminate the deprecation warnings and the error with `override_font`. The application will now display properly with a functional sidebar, editor, and toolbar.

Let's fix the issues in the UI code. We're encountering two main problems:

1. The `override_font` method is not available in GTK 4 (it's from GTK 3)
2. There are deprecated TreeView methods in the sidebar implementation

Here's an updated `main.py` that resolves these issues:

```python
#!/usr/bin/env python3
# -*- coding: utf-8 -*-

import sys
import os
import gi
import pickle

# Setup paths
project_dir = os.path.dirname(os.path.abspath(__file__))
sys.path.insert(0, project_dir)

# Create translation function
def _(text):
    return text

# Make it globally available
import builtins
builtins._ = _

# Create Observable class that's used by Settings
class Observable:
    def __init__(self):
        self.observers = []
        self.change_codes = []

    def add_observer(self, observer):
        if observer not in self.observers:
            self.observers.append(observer)

    def remove_observer(self, observer):
        self.observers.remove(observer)

    def add_change_code(self, change_code, parameter=None):
        self.change_codes.append(change_code)
        for observer in self.observers:
            observer.change_notification(change_code, parameter)

    def get_change_code(self, change_code):
        if change_code in self.change_codes:
            return True
        return False

    def remove_change_code(self, change_code):
        self.change_codes.remove(change_code)

# Define Settings class to be used directly
class Settings(Observable):
    def __init__(self, pathname):
        Observable.__init__(self)
        self.pathname = pathname
        self.data = {}
        self.defaults = {}
        self.set_defaults()

        if not self.unpickle():
            self.data = self.defaults.copy()
            self.pickle()

    def set_defaults(self):
        self.defaults = {
            'preferences': {
                'cleanup_build_files': True,
                'color_scheme': 'classic',
                'use_system_font': True,
                'font_string': 'Monospace 11',
                'show_line_numbers': True,
                'enable_code_folding': True,
                'enable_line_wrapping': True,
                'tab_width': 4,
                'highlight_current_line': True,
                'spaces_instead_of_tabs': True
            }
        }

    def get_value(self, section, item):
        try: 
            return self.data[section][item]
        except KeyError:
            try:
                value = self.defaults[section][item]
                self.set_value(section, item, value)
                return value
            except KeyError:
                return None

    def set_value(self, section, item, value):
        if section not in self.data:
            self.data[section] = {}
        self.data[section][item] = value
        self.add_change_code('settings_changed', (section, item, value))

    def unpickle(self):
        if not os.path.isdir(self.pathname):
            os.makedirs(self.pathname, exist_ok=True)

        try: 
            with open(os.path.join(self.pathname, 'settings.pickle'), 'rb') as filehandle:
                self.data = pickle.load(filehandle)
                return True
        except (IOError, EOFError): 
            return False

    def pickle(self):
        try: 
            with open(os.path.join(self.pathname, 'settings.pickle'), 'wb') as filehandle:
                pickle.dump(self.data, filehandle)
                return True
        except IOError: 
            return False

# Simple color manager for UI colors
class ColorManager:
    main_window = None

    @staticmethod
    def get_ui_color(name):
        if ColorManager.main_window is None:
            # Return fallback color
            return Gdk.RGBA(0.95, 0.95, 0.95, 1.0)

        result = ColorManager.main_window.get_style_context().lookup_color(name)
        if result[0]:  # If lookup successful
            return result[1]
        else:
            # Return fallback color
            return Gdk.RGBA(0.95, 0.95, 0.95, 1.0)

# Now create a custom ServiceLocator that doesn't use the external import
class ServiceLocator:
    main_window = None
    workspace = None
    settings = None
    setzer_version = None
    resources_path = None
    app_icons_path = None
    increments = {}
    regexes = {}
    source_language_manager = None
    source_style_scheme_manager = None

    @staticmethod
    def set_main_window(main_window):
        ServiceLocator.main_window = main_window
        ColorManager.main_window = main_window

    @staticmethod
    def get_main_window():
        return ServiceLocator.main_window

    @staticmethod
    def set_workspace(workspace):
        ServiceLocator.workspace = workspace

    @staticmethod
    def get_workspace():
        return ServiceLocator.workspace

    @staticmethod
    def get_increment(key):
        if key not in ServiceLocator.increments:
            ServiceLocator.increments[key] = 0
        ServiceLocator.increments[key] += 1
        return ServiceLocator.increments[key]

    @staticmethod
    def get_regex_object(pattern):
        import re
        if pattern in ServiceLocator.regexes:
            return ServiceLocator.regexes[pattern]
        else:
            regex = re.compile(pattern)
            ServiceLocator.regexes[pattern] = regex
            return regex

    @staticmethod
    def get_settings():
        if ServiceLocator.settings is None:
            # Use our own Settings class
            from gi.repository import GLib
            config_folder = ServiceLocator.get_config_folder()
            ServiceLocator.settings = Settings(config_folder)
        return ServiceLocator.settings

    @staticmethod
    def get_config_folder():
        from gi.repository import GLib
        return os.path.join(GLib.get_user_config_dir(), 'setzer')

    @staticmethod
    def set_setzer_version(setzer_version):
        ServiceLocator.setzer_version = setzer_version

    @staticmethod
    def get_setzer_version():
        return ServiceLocator.setzer_version

    @staticmethod
    def set_resources_path(resources_path):
        ServiceLocator.resources_path = resources_path

    @staticmethod
    def get_resources_path():
        return ServiceLocator.resources_path

    @staticmethod
    def set_app_icons_path(app_icons_path):
        ServiceLocator.app_icons_path = app_icons_path

    @staticmethod
    def get_app_icons_path():
        return ServiceLocator.app_icons_path

    @staticmethod
    def get_source_language_manager():
        if ServiceLocator.source_language_manager is None:
            from gi.repository import GtkSource
            ServiceLocator.source_language_manager = GtkSource.LanguageManager()
            path = os.path.join(ServiceLocator.get_resources_path(), 'language-specs')
            ServiceLocator.source_language_manager.set_search_path((path,))
        return ServiceLocator.source_language_manager

    @staticmethod
    def get_source_style_scheme_manager():
        if ServiceLocator.source_style_scheme_manager is None:
            from gi.repository import GtkSource
            import os
            ServiceLocator.source_style_scheme_manager = GtkSource.StyleSchemeManager()
            path1 = os.path.join(ServiceLocator.get_resources_path(), 'themes')
            config_folder = ServiceLocator.get_config_folder()
            themes_folder = os.path.join(config_folder, 'themes')
            if not os.path.isdir(themes_folder):
                os.makedirs(themes_folder, exist_ok=True)
            path2 = themes_folder
            ServiceLocator.source_style_scheme_manager.set_search_path((path1, path2))
        return ServiceLocator.source_style_scheme_manager

    @staticmethod
    def get_source_language(language):
        source_language_manager = ServiceLocator.get_source_language_manager()
        if language == 'bibtex': 
            return source_language_manager.get_language('bibtex')
        else: 
            return source_language_manager.get_language('latex')

    @staticmethod
    def get_style_scheme():
        name = ServiceLocator.get_settings().get_value('preferences', 'color_scheme')
        return ServiceLocator.get_source_style_scheme_manager().get_scheme(name)

# Setup GTK
gi.require_version('Gtk', '4.0')
gi.require_version('GtkSource', '5')
gi.require_version('Adw', '1')
from gi.repository import Gtk, GLib, Gio, Adw, GtkSource, Gdk, Pango

# Set application resources path
resources_path = os.path.join(project_dir, 'resources')
if not os.path.exists(resources_path):
    os.makedirs(resources_path, exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'icons'), exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'language-specs'), exist_ok=True)
    os.makedirs(os.path.join(resources_path, 'themes'), exist_ok=True)

app_icons_path = os.path.join(resources_path, 'icons')
ServiceLocator.set_resources_path(resources_path)
ServiceLocator.set_app_icons_path(app_icons_path)
ServiceLocator.set_setzer_version('2.0.0')

# Create a toolbar button with icon
def create_button(icon_name, tooltip_text, clicked_callback=None):
    button = Gtk.Button()
    button.set_tooltip_text(tooltip_text)

    icon = Gtk.Image.new_from_icon_name(icon_name)
    button.set_child(icon)

    if clicked_callback:
        button.connect("clicked", clicked_callback)

    return button

# Create a menu button
def create_menu_button(icon_name, tooltip_text, menu_model):
    button = Gtk.MenuButton()
    button.set_tooltip_text(tooltip_text)

    icon = Gtk.Image.new_from_icon_name(icon_name)
    button.set_child(icon)
    button.set_menu_model(menu_model)

    return button

# Tree item for the document structure sidebar
class DocStructureItem:
    def __init__(self, title, level=0):
        self.title = title
        self.level = level
        self.expanded = False
        self.children = []

    def add_child(self, item):
        self.children.append(item)
        return item

# Enhanced window for testing Setzer UI
class SetzerWindow(Adw.ApplicationWindow):
    def __init__(self, app):
        Adw.ApplicationWindow.__init__(self, application=app)
        self.set_default_size(1100, 700)
        self.set_title("Setzer - LaTeX Editor")

        self.create_css()
        self.create_widgets()
        self.apply_settings()

    def create_css(self):
        # Add CSS for styling
        css_provider = Gtk.CssProvider()
        css = '''
        .sidebar {
            background-color: #f5f5f5;
            border-right: 1px solid #ddd;
        }

        .editor {
            background-color: #ffffff;
        }

        .toolbar {
            background-color: #f8f9fa;
            border-bottom: 1px solid #ddd;
        }

        .statusbar {
            background-color: #f8f9fa;
            border-top: 1px solid #ddd;
            font-size: 0.9em;
        }

        .highlight {
            background-color: #e2eefe;
        }

        .document-item {
            padding: 4px 0;
            margin-left: 12px;
        }
        '''
        css_provider.load_from_data(css.encode())
        Gtk.StyleContext.add_provider_for_display(
            Gdk.Display.get_default(),
            css_provider,
            Gtk.STYLE_PROVIDER_PRIORITY_APPLICATION
        )

    def create_widgets(self):
        # Main layout container
        self.main_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)

        # Create header bar with toolbar
        self.header = self.create_header_bar()

        # Create the main paned container (sidebar and editor area)
        self.main_paned = Gtk.Paned(orientation=Gtk.Orientation.HORIZONTAL)

        # Create sidebar
        self.sidebar = self.create_sidebar()
        self.sidebar.set_size_request(250, -1)

        # Create editor area
        self.editor_box = self.create_editor_area()

        # Add sidebar and editor to paned container
        self.main_paned.set_start_child(self.sidebar)
        self.main_paned.set_end_child(self.editor_box)
        self.main_paned.set_resize_start_child(False)

        # Create status bar
        self.statusbar = self.create_statusbar()

        # Add all components to main box
        self.main_box.append(self.header)
        self.main_box.append(self.main_paned)
        self.main_box.append(self.statusbar)

        # Set content
        self.set_content(self.main_box)

    def create_header_bar(self):
        header = Adw.HeaderBar()
        header.add_css_class("toolbar")

        # Document actions
        new_button = create_button("document-new-symbolic", "New Document", self.on_new_document)
        header.pack_start(new_button)

        open_button = create_button("document-open-symbolic", "Open Document", self.on_open_document)
        header.pack_start(open_button)

        save_button = create_button("document-save-symbolic", "Save Document", self.on_save_document)
        header.pack_start(save_button)

        # Build actions
        build_button = create_button("system-run-symbolic", "Build Document", self.on_build_document)
        header.pack_end(build_button)

        # Settings menu
        menu_model = Gio.Menu()
        menu_model.append("Preferences", "app.preferences")
        menu_model.append("Keyboard Shortcuts", "app.shortcuts")
        menu_model.append("About Setzer", "app.about")

        menu_button = create_menu_button("open-menu-symbolic", "Settings", menu_model)
        header.pack_end(menu_button)

        return header

    def create_sidebar(self):
        sidebar_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)
        sidebar_box.add_css_class("sidebar")

        # Sidebar header
        sidebar_header = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        sidebar_header.set_margin_top(10)
        sidebar_header.set_margin_bottom(10)
        sidebar_header.set_margin_start(10)
        sidebar_header.set_margin_end(10)

        sidebar_label = Gtk.Label(label="Document Structure")
        sidebar_label.set_halign(Gtk.Align.START)
        sidebar_label.set_hexpand(True)

        refresh_button = create_button("view-refresh-symbolic", "Refresh Structure", self.on_refresh_structure)
        refresh_button.set_valign(Gtk.Align.CENTER)

        sidebar_header.append(sidebar_label)
        sidebar_header.append(refresh_button)

        # Document structure list using GTK4 ListBox instead of deprecated TreeView
        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_vexpand(True)

        # Create a ListBox for document structure
        self.structure_list = Gtk.ListBox()
        self.structure_list.set_selection_mode(Gtk.SelectionMode.SINGLE)
        self.structure_list.set_activate_on_single_click(True)
        self.structure_list.connect("row-activated", self.on_structure_item_activated)

        # Populate structure
        self.populate_document_structure()

        scrolled_window.set_child(self.structure_list)

        # Put everything in the sidebar
        sidebar_box.append(sidebar_header)
        sidebar_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL))
        sidebar_box.append(scrolled_window)

        return sidebar_box

    def populate_document_structure(self):
        # Sample document structure
        structure = [
            {"title": "Introduction", "children": [
                {"title": "Background", "children": []},
                {"title": "Motivation", "children": []}
            ]},
            {"title": "Methods", "children": [
                {"title": "Experiment Setup", "children": []},
                {"title": "Data Collection", "children": []}
            ]},
            {"title": "Results", "children": []},
            {"title": "Discussion", "children": []},
            {"title": "Conclusion", "children": []},
            {"title": "References", "children": []}
        ]

        # Clear the list
        while True:
            row = self.structure_list.get_row_at_index(0)
            if row is None:
                break
            self.structure_list.remove(row)

        # Add structure items to the list
        for item in structure:
            self.add_structure_item(item, 0)

    def add_structure_item(self, item, level):
        # Create a row for the item
        row = Gtk.ListBoxRow()

        # Create the content
        box = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        box.set_margin_start(level * 16)  # Indent based on level
        box.set_margin_top(2)
        box.set_margin_bottom(2)

        # Add icon
        icon = Gtk.Image.new_from_icon_name("text-x-generic")
        icon.set_margin_end(8)
        box.append(icon)

        # Add label
        label = Gtk.Label(label=item["title"])
        label.set_halign(Gtk.Align.START)
        label.set_hexpand(True)
        box.append(label)

        row.set_child(box)
        self.structure_list.append(row)

        # Add children
        for child in item["children"]:
            self.add_structure_item(child, level + 1)

    def create_editor_area(self):
        editor_box = Gtk.Box(orientation=Gtk.Orientation.VERTICAL)
        editor_box.add_css_class("editor")

        # Toolbar for common LaTeX commands
        toolbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        toolbar.add_css_class("toolbar")
        toolbar.set_margin_start(5)
        toolbar.set_margin_end(5)
        toolbar.set_margin_top(5)
        toolbar.set_margin_bottom(5)
        toolbar.set_spacing(5)

        # Add LaTeX command buttons
        buttons = [
            ("Bold", "\\textbf{}", "format-text-bold-symbolic"),
            ("Italic", "\\textit{}", "format-text-italic-symbolic"),
            ("Underline", "\\underline{}", "format-text-underline-symbolic"),
            ("Math Mode", "$", "accessories-calculator-symbolic"),
            ("Enumerate", "\\begin{enumerate}\n\\item \n\\end{enumerate}", "view-list-symbolic"),
            ("Itemize", "\\begin{itemize}\n\\item \n\\end{itemize}", "view-list-bullet-symbolic"),
            ("Figure", "\\begin{figure}\n\\centering\n\\includegraphics{}\n\\caption{}\n\\label{fig:}\n\\end{figure}", "insert-image-symbolic"),
            ("Table", "\\begin{table}\n\\centering\n\\caption{}\n\\label{tab:}\n\\begin{tabular}{}\n\\end{tabular}\n\\end{table}", "x-office-spreadsheet-symbolic"),
        ]

        for tooltip, text, icon in buttons:
            button = create_button(icon, tooltip, lambda btn, t=text: self.insert_latex_command(t))
            toolbar.append(button)

        # Source view for LaTeX editing
        scrolled_window = Gtk.ScrolledWindow()
        scrolled_window.set_vexpand(True)

        self.source_view = GtkSource.View()
        self.source_view.set_monospace(True)
        self.source_view.set_auto_indent(True)
        self.source_view.set_indent_width(2)
        self.source_view.set_insert_spaces_instead_of_tabs(True)
        self.source_view.set_tab_width(2)
        self.source_view.set_show_line_numbers(True)
        self.source_view.set_highlight_current_line(True)

        # Set the font using CSS for GTK4
        css_provider = Gtk.CssProvider()
        css = '''
        textview {
            font-family: monospace;
            font-size: 11pt;
        }
        '''
        css_provider.load_from_data(css.encode())
        self.source_view.get_style_context().add_provider(
            css_provider,
            Gtk.STYLE_PROVIDER_PRIORITY_APPLICATION
        )

        # Create and set buffer with LaTeX sample
        self.buffer = GtkSource.Buffer()
        self.buffer.set_text(self.get_sample_document())

        # Set syntax highlighting for LaTeX
        manager = ServiceLocator.get_source_language_manager()
        latex_lang = manager.get_language("latex")
        if latex_lang:
            self.buffer.set_language(latex_lang)

        # Set style scheme
        style_scheme_manager = ServiceLocator.get_source_style_scheme_manager()
        style_scheme = style_scheme_manager.get_scheme("classic")
        if style_scheme:
            self.buffer.set_style_scheme(style_scheme)

        self.source_view.set_buffer(self.buffer)
        scrolled_window.set_child(self.source_view)

        # Add toolbar and editor to the box
        editor_box.append(toolbar)
        editor_box.append(Gtk.Separator(orientation=Gtk.Orientation.HORIZONTAL))
        editor_box.append(scrolled_window)

        return editor_box

    def create_statusbar(self):
        statusbar = Gtk.Box(orientation=Gtk.Orientation.HORIZONTAL)
        statusbar.add_css_class("statusbar")
        statusbar.set_spacing(10)
        statusbar.set_margin_start(10)
        statusbar.set_margin_end(10)
        statusbar.set_margin_top(5)
        statusbar.set_margin_bottom(5)

        status_label = Gtk.Label(label="Ready")
        status_label.set_halign(Gtk.Align.START)
        status_label.set_hexpand(True)

        position_label = Gtk.Label(label="Line: 1, Col: 1")
        position_label.set_halign(Gtk.Align.END)

        # Update position when cursor moves
        self.buffer.connect("notify::cursor-position", lambda buf, param: self.update_cursor_position(position_label))

        statusbar.append(status_label)
        statusbar.append(position_label)

        return statusbar

    def apply_settings(self):
        # Apply settings from ServiceLocator
        settings = ServiceLocator.get_settings()

        # Editor settings - we no longer use override_font since it's deprecated
        # Font setting is handled via CSS above
        self.source_view.set_show_line_numbers(settings.get_value("preferences", "show_line_numbers"))
        self.source_view.set_highlight_current_line(settings.get_value("preferences", "highlight_current_line"))
        self.source_view.set_tab_width(settings.get_value("preferences", "tab_width"))
        self.source_view.set_insert_spaces_instead_of_tabs(settings.get_value("preferences", "spaces_instead_of_tabs"))
        self.source_view.set_wrap_mode(Gtk.WrapMode.WORD if settings.get_value("preferences", "enable_line_wrapping") else Gtk.WrapMode.NONE)

    def update_cursor_position(self, label):
        # Get the cursor position
        cursor_pos = self.buffer.get_property("cursor-position")
        iter = self.buffer.get_iter_at_offset(cursor_pos)
        line = iter.get_line() + 1
        column = iter.get_line_offset() + 1

        # Update the position label
        label.set_text(f"Line: {line}, Col: {column}")

    def get_sample_document(self):
        return r"""\documentclass{article}
\usepackage[utf8]{inputenc}
\usepackage{graphicx}
\usepackage{amsmath}
\usepackage{hyperref}

\title{Sample \LaTeX{} Document}
\author{Setzer User}
\date{\today}

\begin{document}

\maketitle

\section{Introduction}
This is a sample \LaTeX{} document created in Setzer.

\section{Mathematics}
You can write equations like this:
\begin{equation}
    E = mc^2
\end{equation}

Or inline math like $f(x) = x^2$.

\section{Lists}
\begin{itemize}
    \item First item
    \item Second item
    \item Third item
\end{itemize}

\begin{enumerate}
    \item First numbered item
    \item Second numbered item
\end{enumerate}

\section{Figures}
\begin{figure}
    \centering
    \caption{A sample figure}
    \label{fig:sample}
\end{figure}

\section{Conclusion}
This is the end of our sample document.

\end{document}
"""

    # Event handlers
    def on_new_document(self, button):
        self.buffer.set_text("\\documentclass{article}\n\\begin{document}\n\n\\end{document}")
        print("New document created")

    def on_open_document(self, button):
        print("Open document clicked")
        # Here you would implement a file chooser dialog

    def on_save_document(self, button):
        print("Save document clicked")
        # Here you would implement saving functionality

    def on_build_document(self, button):
        print("Build document clicked")
        # Here you would implement LaTeX compilation

    def on_refresh_structure(self, button):
        print("Refresh structure clicked")
        self.populate_document_structure()

    def on_structure_item_activated(self, list_box, row):
        index = row.get_index()
        print(f"Selected item at index: {index}")
        # Here you would jump to the corresponding position in the document

    def insert_latex_command(self, command):
        self.buffer.begin_user_action()
        self.buffer.insert_at_cursor(command)
        self.buffer.end_user_action()
        print(f"Inserted: {command}")

class SetzerApp(Gtk.Application):
    def __init__(self):
        Gtk.Application.__init__(self, 
                               application_id="org.setzer.editor",
                               flags=Gio.ApplicationFlags.NON_UNIQUE)

        self.connect("activate", self.on_activate)

    def on_activate(self, app):
        try:
            self.window = SetzerWindow(self)
            ServiceLocator.set_main_window(self.window)

            # Add application actions
            self.create_actions()

            self.window.present()
            print("Setzer application started successfully!")

        except Exception as e:
            print(f"Error in activation: {e}")
            import traceback
            traceback.print_exc()

    def create_actions(self):
        # Add application actions for menu items
        actions = [
            ("preferences", self.on_preferences),
            ("shortcuts", self.on_shortcuts),
            ("about", self.on_about)
        ]

        for name, callback in actions:
            action = Gio.SimpleAction.new(name, None)
            action.connect("activate", callback)
            self.add_action(action)

    def on_preferences(self, action, param):
        print("Preferences menu item clicked")
        # Here you would open a preferences dialog

    def on_shortcuts(self, action, param):
        print("Keyboard shortcuts menu item clicked")
        # Here you would show a shortcuts window

    def on_about(self, action, param):
        print("About menu item clicked")
        # Here you would display an about dialog

def main():
    try:
        print(f"Running as PID: {os.getpid()}")
        app = SetzerApp()
        exit_status = app.run(sys.argv)
        sys.exit(exit_status)
    except Exception as e:
        print(f"Error in main: {e}")
        import traceback
        traceback.print_exc()
        sys.exit(1)

if __name__ == "__main__":
    main()
```

Key changes:

1. **Replaced TreeView with ListBox** - Instead of using the deprecated GTK.TreeView and its related methods, we're now using a GTK4-compatible Gtk.ListBox for the document structure sidebar.

2. **Fixed font setting** - Removed the deprecated `override_font` method and replaced it with CSS styling for the text view.

3. **Restructured document structure handling** - Created a cleaner structure for the document elements that works with GTK4.

4. **Updated event handling** - Modified how selection in the document structure works.

These changes should eliminate the deprecation warnings and the error with `override_font`. The application will now display properly with a functional sidebar, editor, and toolbar.

