class PreviewView(Gtk.Box):
def __init__(self): Gtk.Box.__init__(self) self.set_orientation(Gtk.Orientation.VERTICAL) self.get_style_context().add_class('preview')
self.content = ScrollingWidget() self.drawing_area = self.content.content
self.blank_slate = BlankSlateView()
self.stack = Gtk.Stack() self.stack.set_vexpand(True) self.stack.add_named(self.blank_slate, 'blank_slate') self.stack.add_named(self.content.view, 'pdf')
self.overlay = Gtk.Overlay() self.overlay.set_vexpand(True) self.overlay.set_child(self.stack) self.append(self.overlay)
self.target_label = Gtk.Label() self.target_label.set_halign(Gtk.Align.START) self.target_label.set_valign(Gtk.Align.END) self.target_label.set_can_target(False) self.target_label.get_style_context().add_class('target-label') self.overlay.add_overlay(self.target_label) self.set_link_target_string('')
def set_layout_data(self, layout_data): self.layout_data = layout_data
def set_link_target_string(self, target_string): self.target_label.set_text(target_string) self.target_label.set_visible(target_string != '')

@staticmethod
    def get_ui_color(name):
        if ColorManager.main_window is None:
            # Return fallback color
            return Gdk.RGBA(0.95, 0.95, 0.95, 1.0)

        # Get the CSS classes of the widget
        css_classes = ColorManager.main_window.get_css_classes()
        # In GTK4, direct color lookup is no longer available,
        # so we return a default color
        return Gdk.RGBA(0.95, 0.95, 0.95, 1.0)

