class DialogView(Gtk.Window):
def __init__(self, main_window): Gtk.Window.__init__(self)
self.set_modal(True) self.set_transient_for(main_window) self.set_destroy_with_parent(True)
self.headerbar = Gtk.HeaderBar() self.set_titlebar(self.headerbar)
self.topbox = Gtk.Box.new(Gtk.Orientation.VERTICAL, 0) self.set_child(self.topbox)
self.shortcuts_controller = ShortcutController() self.shortcuts_controller.create_and_add_shortcut('Escape', self.close) self.add_controller(self.shortcuts_controller)

