import gi gi.require_version('Gtk', '4.0') from gi.repository import Gtk
import dialogs.preferences.preferences_viewgtk as view import setzer.dialogs.preferences.pages.page_build_system as page_build_system import setzer.dialogs.preferences.pages.page_editor as page_editor import setzer.dialogs.preferences.pages.page_font_color as page_font_color import setzer.dialogs.preferences.pages.page_autocomplete as page_autocomplete from setzer.app.service_locator import ServiceLocator
class PreferencesDialog(object):
def __init__(self, main_window): self.main_window = main_window self.settings = ServiceLocator.get_settings()
def run(self): self.setup() self.view.present()
def setup(self): self.view = view.Preferences(self.main_window)
self.page_build_system = page_build_system.PageBuildSystem(self, self.settings) self.page_editor = page_editor.PageEditor(self, self.settings) self.page_font_color = page_font_color.PageFontColor(self, self.settings, self.main_window) self.page_autocomplete = page_autocomplete.PageAutocomplete(self, self.settings)
self.view.notebook.append_page(self.page_build_system.view, Gtk.Label.new(_('Build System'))) self.view.notebook.append_page(self.page_editor.view, Gtk.Label.new(_('Editor'))) self.view.notebook.append_page(self.page_font_color.view, Gtk.Label.new(_('Font & Colors'))) self.view.notebook.append_page(self.page_autocomplete.view, Gtk.Label.new(_('Autocomplete')))
self.page_build_system.init() self.page_editor.init() self.page_font_color.init() self.page_autocomplete.init()
def on_check_button_toggle(self, button, preference_name): self.settings.set_value('preferences', preference_name, button.get_active())
def on_radio_button_toggle(self, button, preference_name, value): self.settings.set_value('preferences', preference_name, value)
def spin_button_changed(self, button, preference_name): self.settings.set_value('preferences', preference_name, button.get_value_as_int())
def text_deleted(self, buffer, position, n_chars, preference_name): self.settings.set_value('preferences', preference_name, buffer.get_text())
def text_inserted(self, buffer, position, chars, n_chars, preference_name): self.settings.set_value('preferences', preference_name, buffer.get_text())
def on_interpreter_changed(self, button, preference_name, value): self.settings.set_value('preferences', preference_name, value)
