```java
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.util.HashMap;
import java.util.Map;

public class SyntaxHighlighter {

    // Map file extensions to language names
    private final Map<String, String> languageMap = new HashMap<>();

    public SyntaxHighlighter() {
        // Initialize language mappings
        languageMap.put(".java", "Java");
        languageMap.put(".py", "Python");
        languageMap.put(".js", "JavaScript");
        languageMap.put(".ts", "TypeScript");
        languageMap.put(".html", "HTML");
        languageMap.put(".css", "CSS");
        languageMap.put(".c", "C");
        languageMap.put(".cpp", "C++");
        languageMap.put(".h", "C");
        languageMap.put(".cs", "CSharp");
        languageMap.put(".go", "Go");
        languageMap.put(".rs", "Rust");
        languageMap.put(".php", "PHP");
        languageMap.put(".rb", "Ruby");
        languageMap.put(".kt", "Kotlin");
        languageMap.put(".swift", "Swift");
    }

    public String highlightCode(File file) throws IOException {
        String content = Files.readString(file.toPath());
        String extension = getFileExtension(file.getName());
        String language = languageMap.getOrDefault(extension, "plaintext");

        // Simple HTML wrapping for code
        String htmlContent = "<pre><code class=\"language-" + language + "\">" +
                escapeHtml(content) +
                "</code></pre>";

        return htmlContent;
    }

    private String getFileExtension(String filename) {
        int lastDotIndex = filename.lastIndexOf('.');
        if (lastDotIndex > 0) {
            return filename.substring(lastDotIndex).toLowerCase();
        }
        return "";
    }

    private String escapeHtml(String content) {
        return content
            .replace("&", "&amp;")
            .replace("<", "&lt;")
            .replace(">", "&gt;")
            .replace("\"", "&quot;")
            .replace("'", "&#39;");
    }
}
```

```java
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;
import java.util.stream.Collectors;
import java.util.stream.Stream;

public class FileScanner {

    // File extensions to include
    private final List<String> codeExtensions = List.of(
        ".java", ".py", ".js", ".ts", ".html", ".css", ".c", ".cpp", 
        ".h", ".cs", ".go", ".rs", ".php", ".rb", ".kt", ".swift"
    );

    // Directories to exclude
    private final List<String> excludeDirs = List.of(
        "node_modules", "target", "build", "dist", ".git", ".idea", 
        "venv", "__pycache__", "bin", "obj"
    );

    public List<File> scanDirectory(Path directory) throws IOException {
        try (Stream<Path> pathStream = Files.walk(directory)) {
            return pathStream
                .filter(Files::isRegularFile)
                .filter(this::isCodeFile)
                .filter(path -> !isInExcludedDir(path))
                .map(Path::toFile)
                .collect(Collectors.toList());
        }
    }

    private boolean isCodeFile(Path path) {
        String fileName = path.toString().toLowerCase();
        return codeExtensions.stream().anyMatch(fileName::endsWith);
    }

    private boolean isInExcludedDir(Path path) {
        return excludeDirs.stream()
            .anyMatch(dir -> path.toString().contains(File.separator + dir + File.separator));
    }
}
