echo "Directory: $DIR_PATH"
echo "Output: $OUTPUT_FILE"

java -cp "$JAR_FILE:$LIB_DIR/*" Main "$DIR_PATH" "$OUTPUT_FILE"

if [ $? -eq 0 ]; then
    echo -e "${GREEN}PDF created successfully: $OUTPUT_FILE${NC}"

    # Try to open PDF if xdg-open is available
    if command -v xdg-open &> /dev/null; then
        echo "Opening PDF..."
        xdg-open "$OUTPUT_FILE"
    fi
else
    echo -e "${RED}Error creating PDF. See above for details.${NC}"
fi
