# Define the location for our app
APP_DIR="$HOME/.local/share/codepdf"
JAR_FILE="$APP_DIR/codepdf.jar"
LIB_DIR="$APP_DIR/lib"

# Check if installation is needed
if [ ! -f "$JAR_FILE" ]; then
    echo -e "${GREEN}First-time setup: Installing CodePDF...${NC}"

    # Create directories
    mkdir -p "$APP_DIR"
    mkdir -p "$LIB_DIR"

    # Download required JAR files
    echo "Downloading required libraries..."

    # Download iText Core
    curl -L "https://repo1.maven.org/maven2/com/itextpdf/itextpdf/5.5.13.3/itextpdf-5.5.13.3.jar" -o "$LIB_DIR/itextpdf-5.5.13.3.jar"

    # Download XML Worker
    curl -L "https://repo1.maven.org/maven2/com/itextpdf/tool/xmlworker/5.5.13.3/xmlworker-5.5.13.3.jar" -o "$LIB_DIR/xmlworker-5.5.13.3.jar"

    # Create the script to compile our code
    COMPILE_SCRIPT="$APP_DIR/compile.sh"

    cat > "$COMPILE_SCRIPT" << 'EOF'
#!/bin/bash
APP_DIR="$HOME/.local/share/codepdf"
LIB_DIR="$APP_DIR/lib"
SRC_DIR="$APP_DIR/src"
CLASSES_DIR="$APP_DIR/classes"

# Create dirs
mkdir -p "$CLASSES_DIR"

# Compile
javac -cp "$LIB_DIR/*" -d "$CLASSES_DIR" "$SRC_DIR"/*.java

# Create JAR
jar cf "$APP_DIR/codepdf.jar" -C "$CLASSES_DIR" .
EOF

    chmod +x "$COMPILE_SCRIPT"

    # Create source directory and copy source files
    mkdir -p "$APP_DIR/src"

    # Copy our Java source files
    cp -f FileScanner.java SyntaxHighlighter.java PDFGenerator.java Main.java "$APP_DIR/src/" 2>/dev/null || {
        # If files weren't found, create them
        # Create Main.java
        cat > "$APP_DIR/src/Main.java" << 'EOF'
import java.io.File;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        try {
            if (args.length < 1) {
                System.err.println("Usage: codepdf <directory_path> [output_file.pdf]");
                System.exit(1);
            }

            String path = args[0];
            // Check if the path exists and is a directory
            File directory = new File(path);
            if (!directory.exists() || !directory.isDirectory()) {
                System.err.println("Error: The specified path is not a valid directory.");
                System.exit(1);
            }

            // Default output name is the directory name + .pdf
            String outputFile = directory.getName() + ".pdf";
            if (args.length >= 2) {
                outputFile = args[1];
            }

            // If output doesn't end with .pdf, add it
            if (!outputFile.toLowerCase().endsWith(".pdf")) {
                outputFile += ".pdf";
            }

            // If output path is not absolute, place it in the parent directory of the input
            File outputPath = new File(outputFile);
            if (!outputPath.isAbsolute()) {
                outputPath = new File(directory.getParentFile(), outputFile);
                outputFile = outputPath.getAbsolutePath();
            }

            System.out.println("Starting codebase to PDF conversion...");
            System.out.println("Source directory: " + path);
            System.out.println("Output file: " + outputFile);

            // Create components
            FileScanner scanner = new FileScanner();
            SyntaxHighlighter highlighter = new SyntaxHighlighter();
            PDFGenerator pdfGenerator = new PDFGenerator();

            // Scan for code files
            List<File> codeFiles = scanner.scanDirectory(Paths.get(path));
            System.out.println("Found " + codeFiles.size() + " code files.");

            // Generate PDF
            pdfGenerator.generatePDF(codeFiles, highlighter, outputFile);

            System.out.println("PDF generation complete! Output saved to: " + outputFile);

        } catch (Exception e) {
            System.err.println("Error generating PDF: " + e.getMessage());
            e.printStackTrace();
        }
    }
}
EOF

        # Create FileScanner.java
        cat > "$APP_DIR/src/FileScanner.java" << 'EOF'
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;
import java.util.stream.Collectors;
import java.util.stream.Stream;

public class FileScanner {

    // File extensions to include
    private final List<String> codeExtensions = List.of(
        ".java", ".py", ".js", ".ts", ".html", ".css", ".c", ".cpp", 
        ".h", ".cs", ".go", ".rs", ".php", ".rb", ".kt", ".swift"
    );

    // Directories to exclude
    private final List<String> excludeDirs = List.of(
        "node_modules", "target", "build", "dist", ".git", ".idea", 
        "venv", "__pycache__", "bin", "obj"
    );

    public List<File> scanDirectory(Path directory) throws IOException {
        try (Stream<Path> pathStream = Files.walk(directory)) {
            return pathStream
                .filter(Files::isRegularFile)
                .filter(this::isCodeFile)
                .filter(path -> !isInExcludedDir(path))
                .map(Path::toFile)
                .collect(Collectors.toList());
        }
    }

    private boolean isCodeFile(Path path) {
        String fileName = path.toString().toLowerCase();
        return codeExtensions.stream().anyMatch(fileName::endsWith);
    }

    private boolean isInExcludedDir(Path path) {
        return excludeDirs.stream()
            .anyMatch(dir -> path.toString().contains(File.separator + dir + File.separator));
    }
}
EOF

        # Create SyntaxHighlighter.java
        cat > "$APP_DIR/src/SyntaxHighlighter.java" << 'EOF'
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.util.HashMap;
import java.util.Map;

public class SyntaxHighlighter {

    // Map file extensions to language names
    private final Map<String, String> languageMap = new HashMap<>();

    public SyntaxHighlighter() {
        // Initialize language mappings
        languageMap.put(".java", "Java");
        languageMap.put(".py", "Python");
        languageMap.put(".js", "JavaScript");
        languageMap.put(".ts", "TypeScript");
        languageMap.put(".html", "HTML");
        languageMap.put(".css", "CSS");
        languageMap.put(".c", "C");
        languageMap.put(".cpp", "C++");
        languageMap.put(".h", "C");
        languageMap.put(".cs", "CSharp");
        languageMap.put(".go", "Go");
        languageMap.put(".rs", "Rust");
        languageMap.put(".php", "PHP");
        languageMap.put(".rb", "Ruby");
        languageMap.put(".kt", "Kotlin");
        languageMap.put(".swift", "Swift");
    }

    public String highlightCode(File file) throws IOException {
        String content = Files.readString(file.toPath());
        String extension = getFileExtension(file.getName());
        String language = languageMap.getOrDefault(extension, "plaintext");

        // Simple HTML wrapping for code
        String htmlContent = "<pre><code class=\"language-" + language + "\">" +
                escapeHtml(content) +
                "</code></pre>";

        return htmlContent;
    }

    private String getFileExtension(String filename) {
        int lastDotIndex = filename.lastIndexOf('.');
        if (lastDotIndex > 0) {
            return filename.substring(lastDotIndex).toLowerCase();
        }
        return "";
    }

    private String escapeHtml(String content) {
        return content
            .replace("&", "&amp;")
            .replace("<", "&lt;")
            .replace(">", "&gt;")
            .replace("\"", "&quot;")
            .replace("'", "&#39;");
    }
}
EOF

