## Let's create a standalone script:

```bash
#!/bin/bash

# Define colors for output
GREEN='\033[0;32m'
RED='\033[0;31m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

echo -e "${BLUE}CodePDF - Convert Code to PDF with Line Numbers${NC}"
echo "-----------------------------------------------"

# Check if Java is installed
if ! command -v java &> /dev/null; then
    echo -e "${RED}Error: Java is not installed.${NC}"
    echo "Please install Java to use this tool."
    exit 1
fi

# Create a temporary directory for our files
TEMP_DIR=$(mktemp -d)
echo "Setting up temporary directory: $TEMP_DIR"

# Download required libraries
echo "Downloading required libraries..."
curl -s -L "https://repo1.maven.org/maven2/com/itextpdf/itextpdf/5.5.13.3/itextpdf-5.5.13.3.jar" -o "$TEMP_DIR/itextpdf.jar"
curl -s -L "https://repo1.maven.org/maven2/com/itextpdf/tool/xmlworker/5.5.13.3/xmlworker-5.5.13.3.jar" -o "$TEMP_DIR/xmlworker.jar"

# Create our Java source files
echo "Creating source files..."

# FileScanner.java
cat > "$TEMP_DIR/FileScanner.java" << 'EOF'
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.stream.Collectors;
import java.util.stream.Stream;

public class FileScanner {

    // File extensions to include
    private final List<String> codeExtensions = Arrays.asList(
        ".java", ".py", ".js", ".ts", ".html", ".css", ".c", ".cpp", 
        ".h", ".cs", ".go", ".rs", ".php", ".rb", ".kt", ".swift",
        ".md", ".txt", ".xml", ".json", ".yaml", ".yml", ".sh", ".bat"
    );

    // Directories to exclude
    private final List<String> excludeDirs = Arrays.asList(
        "node_modules", "target", "build", "dist", ".git", ".idea", 
        "venv", "__pycache__", "bin", "obj"
    );

    public List<File> scanDirectory(Path directory) throws IOException {
        List<File> result = new ArrayList<>();
        scanDirectoryRecursive(directory.toFile(), result);
        return result;
    }

    private void scanDirectoryRecursive(File dir, List<File> results) {
        if (dir == null || !dir.exists() || shouldExcludeDir(dir)) {
            return;
        }

        File[] files = dir.listFiles();
        if (files == null) {
            return;
        }

        for (File file : files) {
            if (file.isDirectory()) {
                scanDirectoryRecursive(file, results);
            } else if (isCodeFile(file)) {
                results.add(file);
            }
        }
    }

    private boolean isCodeFile(File file) {
        String fileName = file.getName().toLowerCase();
        for (String ext : codeExtensions) {
            if (fileName.endsWith(ext)) {
                return true;
            }
        }
        return false;
    }

    private boolean shouldExcludeDir(File dir) {
        String dirName = dir.getName();
        for (String excludeDir : excludeDirs) {
            if (dirName.equals(excludeDir)) {
                return true;
            }
        }
        return false;
    }
}
EOF

# PDFGenerator.java
cat > "$TEMP_DIR/PDFGenerator.java" << 'EOF'
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.nio.file.Files;
import java.util.List;

import com.itextpdf.text.Document;
import com.itextpdf.text.DocumentException;
import com.itextpdf.text.Element;
import com.itextpdf.text.Font;
import com.itextpdf.text.FontFactory;
import com.itextpdf.text.PageSize;
import com.itextpdf.text.Paragraph;
import com.itextpdf.text.pdf.PdfPCell;
import com.itextpdf.text.pdf.PdfPTable;
import com.itextpdf.text.pdf.PdfWriter;
import com.itextpdf.text.BaseColor;

public class PDFGenerator {

    public void generatePDF(List<File> files, String outputFile) 
            throws IOException, DocumentException {

        // Create document in landscape mode with margins
        Document document = new Document(PageSize.A4.rotate(), 36, 36, 54, 36);
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(outputFile));
        document.open();

        // Add a title
        Font titleFont = FontFactory.getFont(FontFactory.HELVETICA_BOLD, 18);
        Paragraph title = new Paragraph("Codebase Documentation", titleFont);
        title.setAlignment(Element.ALIGN_CENTER);
        title.setSpacingAfter(20);
        document.add(title);

        // Add table of contents
        addTableOfContents(document, files);

        // Process each file
        for (File file : files) {
            try {
                // Add file name as heading with relative path if possible
                Font headingFont = FontFactory.getFont(FontFactory.HELVETICA_BOLD, 12);
                Paragraph heading = new Paragraph(file.getPath(), headingFont);
                heading.setSpacingBefore(30);
                heading.setSpacingAfter(10);
                document.add(heading);

                // Read file content
                String content = Files.readString(file.toPath());
                String[] lines = content.split("\\r?\\n");

                // Create a table with two columns (line numbers and code)
                PdfPTable table = new PdfPTable(new float[]{0.05f, 0.95f});
                table.setWidthPercentage(100);
                table.setSpacingBefore(10);

                // Set table properties 
                table.getDefaultCell().setPadding(4);
                table.getDefaultCell().setBorderWidth(0);

                // Font settings
                Font lineNumberFont = FontFactory.getFont(FontFactory.COURIER, 8);
                Font codeFont = FontFactory.getFont(FontFactory.COURIER, 9);

                // Add each line with line number
                for (int i = 0; i < lines.length; i++) {
                    // Line number cell (right-aligned with gray background)
                    PdfPCell lineNumberCell = new PdfPCell(new Paragraph(String.valueOf(i + 1), lineNumberFont));
                    lineNumberCell.setHorizontalAlignment(Element.ALIGN_RIGHT);
                    lineNumberCell.setPadding(4);
                    lineNumberCell.setPaddingRight(8);
                    lineNumberCell.setBorderWidth(0);
                    lineNumberCell.setBackgroundColor(new BaseColor(240, 240, 240));
                    table.addCell(lineNumberCell);

                    // Code line cell (with preserved whitespace)
                    String codeLine = lines[i];
                    if (codeLine.isEmpty()) {
                        codeLine = " "; // Preserve empty lines
                    }

                    PdfPCell codeCell = new PdfPCell(new Paragraph(codeLine, codeFont));
                    codeCell.setPadding(4);
                    codeCell.setPaddingLeft(8);
                    codeCell.setBorderWidth(0);

                    // Alternate row coloring for better readability
                    if (i % 2 == 1) {
                        codeCell.setBackgroundColor(new BaseColor(252, 252, 252));
                    }

                    table.addCell(codeCell);
                }

                // Add the table to the document
                document.add(table);

            } catch (Exception e) {
                document.add(new Paragraph("Error processing file: " + file.getPath() + 
                    " - " + e.getMessage()));
            }
        }

        document.close();
    }

    private void addTableOfContents(Document document, List<File> files) throws DocumentException {
        Font tocFont = FontFactory.getFont(FontFactory.HELVETICA, 10);
        Font tocTitleFont = FontFactory.getFont(FontFactory.HELVETICA_BOLD, 14);

        Paragraph tocTitle = new Paragraph("Table of Contents", tocTitleFont);
        tocTitle.setAlignment(Element.ALIGN_CENTER);
        tocTitle.setSpacingAfter(10);
        document.add(tocTitle);

        for (File file : files) {
            document.add(new Paragraph(file.getPath(), tocFont));
        }

        document.add(new Paragraph(" "));
        Paragraph separator = new Paragraph("---");
        separator.setAlignment(Element.ALIGN_CENTER);
        document.add(separator);
        document.add(new Paragraph(" "));
    }
}
EOF

# Main.java
cat > "$TEMP_DIR/Main.java" << 'EOF'
import java.io.File;
import java.nio.file.Paths;
import java.util.List;

public class Main {
    public static void main(String[] args) {
        try {
            if (args.length < 1) {
                System.err.println("Usage: java -cp .:itextpdf.jar:xmlworker.jar Main <directory_path> [output_file.pdf]");
                System.exit(1);
            }

            String path = args[0];
            // Check if the path exists and is a directory
            File directory = new File(path);
            if (!directory.exists() || !directory.isDirectory()) {
                System.err.println("Error: The specified path is not a valid directory.");
                System.exit(1);
            }

            // Default output name is the directory name + .pdf
            String outputFile = directory.getName() + ".pdf";
            if (args.length >= 2) {
                outputFile = args[1];
            }

            // If output doesn't end with .pdf, add it
            if (!outputFile.toLowerCase().endsWith(".pdf")) {
                outputFile += ".pdf";
            }

            // If output path is not absolute, place it in the current directory
            File outputPath = new File(outputFile);
            if (!outputPath.isAbsolute()) {
                outputFile = new File(System.getProperty("user.dir"), outputFile).getPath();
            }

            System.out.println("Starting codebase to PDF conversion...");
            System.out.println("Source directory: " + path);
            System.out.println("Output file: " + outputFile);

            // Create components
            FileScanner scanner = new FileScanner();
            PDFGenerator pdfGenerator = new PDFGenerator();

            // Scan for code files
            List<File> codeFiles = scanner.scanDirectory(Paths.get(path));
            System.out.println("Found " + codeFiles.size() + " code files.");

            // Generate PDF
            pdfGenerator.generatePDF(codeFiles, outputFile);

            System.out.println("PDF generation complete! Output saved to: " + outputFile);

        } catch (Exception e) {
            System.err.println("Error generating PDF: " + e.getMessage());
            e.printStackTrace();
        }
    }
}
EOF

# Compile Java code
echo "Compiling Java code..."
cd "$TEMP_DIR"
javac -cp "itextpdf.jar:xmlworker.jar" *.java

# Check if compilation was successful
if [ $? -ne 0 ]; then
    echo -e "${RED}Compilation failed. Aborting.${NC}"
    exit 1
fi

# Get directory path from arguments or use current directory
DIR_PATH="."
if [ $# -ge 1 ]; then
    DIR_PATH="$1"
fi

# Validate the directory
if [ ! -d "$DIR_PATH" ]; then
    echo -e "${RED}Error: '$DIR_PATH' is not a valid directory${NC}"
    exit 1
fi

# Get absolute path
if [[ "$DIR_PATH" != /* ]]; then
    DIR_PATH="$(pwd)/$DIR_PATH"
fi

# Get output file path
if [ $# -ge 2 ]; then
    OUTPUT_FILE="$2"
else
    DIR_NAME=$(basename "$DIR_PATH")
    OUTPUT_FILE="$(pwd)/${DIR_NAME}.pdf"
fi

# Make sure output file has .pdf extension
if [[ ! "$OUTPUT_FILE" == *.pdf ]]; then
    OUTPUT_FILE="${OUTPUT_FILE}.pdf"
fi

# Run the Java program
echo -e "${GREEN}Converting directory to PDF...${NC}"
echo "Directory: $DIR_PATH"
echo "Output: $OUTPUT_FILE"

java -cp ".:itextpdf.jar:xmlworker.jar" Main "$DIR_PATH" "$OUTPUT_FILE"

# Check if the PDF was successfully created
if [ -f "$OUTPUT_FILE" ]; then
    echo -e "${GREEN}PDF successfully created: $OUTPUT_FILE${NC}"

    # Try to open the PDF if xdg-open is available
    if command -v xdg-open &> /dev/null; then
        echo "Opening PDF..."
        xdg-open "$OUTPUT_FILE"
    fi
else
    echo -e "${RED}Failed to create PDF${NC}"
fi

# Clean up
echo "Cleaning up temporary files..."
rm -rf "$TEMP_DIR"

echo -e "${GREEN}Done!${NC}"
