```java
package org.example;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

/**
 * Utility class for preserving exact code formatting when converting to PDF.
 * Handles tabs, spaces, and all whitespace exactly as they appear in the source file.
 */
public class CodeFormattingPreserver {

    /**
     * Reads a source code file and preserves its exact formatting.
     * 
     * @param filePath Path to the source code file
     * @return List of lines with exact formatting preserved
     * @throws IOException If there's an error reading the file
     */
    public static List<String> readCodeWithExactFormatting(String filePath) throws IOException {
        List<String> lines = new ArrayList<>();

        try (BufferedReader reader = new BufferedReader(new FileReader(filePath))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Store the raw line exactly as it is, preserving all whitespace
                lines.add(line);
            }
        }

        return lines;
    }

    /**
     * Converts tabs to a fixed number of spaces for consistent rendering.
     * 
     * @param line The line of code with potential tabs
     * @param tabSize Number of spaces per tab
     * @return Line with tabs converted to spaces
     */
    public static String convertTabsToSpaces(String line, int tabSize) {
        StringBuilder result = new StringBuilder(line.length() * 2);

        for (int i = 0; i < line.length(); i++) {
            char c = line.charAt(i);
            if (c == '\t') {
                // Calculate how many spaces to add
                int spacesToAdd = tabSize - (result.length() % tabSize);
                result.append(" ".repeat(spacesToAdd));
            } else {
                result.append(c);
            }
        }

        return result.toString();
    }

    /**
     * Creates a monospaced HTML representation of code that preserves exact formatting.
     * 
     * @param lines List of code lines with preserved formatting
     * @param useTabsAsIs Whether to keep tabs or convert them to spaces
     * @param tabSize Number of spaces per tab if converting
     * @return HTML string with preserved formatting
     */
    public static String createFormattingPreservedHtml(List<String> lines, boolean useTabsAsIs, int tabSize) {
        StringBuilder html = new StringBuilder();

        html.append("<pre style=\"font-family: 'Courier New', monospace; white-space: pre; tab-size: ").append(tabSize).append(";\">\n");

        for (int i = 0; i < lines.size(); i++) {
            String line = lines.get(i);

            // Optional tab conversion
            if (!useTabsAsIs) {
                line = convertTabsToSpaces(line, tabSize);
            }

            // Replace special HTML characters
            line = line.replace("&", "&amp;")
                       .replace("<", "&lt;")
                       .replace(">", "&gt;");

            // Add line number
            String lineNumber = String.format("%4d", i + 1);
            html.append("<span style=\"color: #888888; user-select: none;\">")
                .append(lineNumber)
                .append(" | </span>");

            // Add code with formatting preserved
            html.append(line).append("\n");
        }

        html.append("</pre>");
        return html.toString();
    }

    /**
     * Returns CSS styles for preserving code formatting in PDFs.
     * 
     * @return CSS styles as a string
     */
    public static String getFormattingPreservationCss() {
        return "pre, code { font-family: 'Courier New', monospace !important; }\n" +
               "pre { white-space: pre !important; overflow-x: visible !important; }\n" +
               "code { tab-size: 4; -moz-tab-size: 4; }\n" +
               ".line-number { color: #888888; user-select: none; }\n" +
               ".code-container { width: 100%; overflow-x: auto; }\n";
    }
}
