package org.example;

import com.itextpdf.html2pdf.ConverterProperties;
import com.itextpdf.html2pdf.HtmlConverter;
import com.itextpdf.kernel.geom.PageSize;
import com.itextpdf.kernel.pdf.PdfDocument;
import com.itextpdf.kernel.pdf.PdfWriter;
import com.itextpdf.layout.Document;
import com.itextpdf.layout.element.Paragraph;
import com.itextpdf.layout.properties.TextAlignment;

import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;

public class Main {
    public static void main(String[] args) {
        if (args.length < 1) {
            System.out.println("Usage: java -jar codepdf.jar <sourceFile> [outputFile] [language]");
            return;
        }

        String sourceFilePath = args[0];
        String outputFilePath;
        String language;

        if (args.length > 1) {
            outputFilePath = args[1];
        } else {
            outputFilePath = sourceFilePath.substring(0, sourceFilePath.lastIndexOf('.')) + ".pdf";
        }

        if (args.length > 2) {
            language = args[2];
        } else {
            String extension = sourceFilePath.substring(sourceFilePath.lastIndexOf('.') + 1).toLowerCase();
            language = detectLanguageFromExtension(extension);
        }

        try {
            convertCodeToPdf(sourceFilePath, outputFilePath, language);
            System.out.println("Conversion complete. PDF saved to: " + outputFilePath);
        } catch (Exception e) {
            System.err.println("Error converting file: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public static void convertCodeToPdf(String sourceFilePath, String outputFilePath, String language) throws IOException {
        // Read the code file
        StringBuilder codeContent = new StringBuilder();
        try (BufferedReader reader = new BufferedReader(new FileReader(sourceFilePath))) {
            String line;
            int lineNumber = 1;
            while ((line = reader.readLine()) != null) {
                codeContent.append(String.format("%4d | %s\n", lineNumber++, escapeHtml(line)));
            }
        }

        // Create HTML with syntax highlighting
        String html = createHtmlContent(codeContent.toString(), language);

        // Convert HTML to PDF
        PdfWriter pdfWriter = new PdfWriter(outputFilePath);
        PdfDocument pdfDocument = new PdfDocument(pdfWriter);
        PageSize pageSize = PageSize.A4.rotate(); // Landscape orientation for more code per line
        pdfDocument.setDefaultPageSize(pageSize);

        ConverterProperties converterProperties = new ConverterProperties();
        Document document = new Document(pdfDocument);

        // Add a title
        String fileName = new File(sourceFilePath).getName();
        Paragraph title = new Paragraph("Source Code: " + fileName)
                .setBold()
                .setFontSize(14)
                .setTextAlignment(TextAlignment.CENTER)
                .setMarginBottom(20);
        document.add(title);

        // Add the code content
        HtmlConverter.convertToPdf(html, pdfDocument, converterProperties);

        document.close();
    }

    private static String createHtmlContent(String code, String language) {
        return String.format("""
                <!DOCTYPE html>
                <html>
                <head>
                    <style>
                        body {
                            font-family: monospace;
                            white-space: pre;
                            margin: 20px;
                        }
                        pre {
                            background-color: #f5f5f5;
                            padding: 10px;
                            border-radius: 5px;
                            overflow-x: auto;
                        }
                    </style>
                </head>
                <body>
                    <pre><code class="language-%s">%s</code></pre>
                </body>
                </html>
                """, language, code);
    }

    private static String escapeHtml(String str) {
        return str.replace("&", "&amp;")
                  .replace("<", "&lt;")
                  .replace(">", "&gt;");
    }

    private static String detectLanguageFromExtension(String extension) {
        switch (extension) {
            case "java":
                return "java";
            case "py":
                return "python";
            case "js":
                return "javascript";
            case "html":
            case "htm":
                return "html";
            case "css":
                return "css";
            case "xml":
                return "xml";
            case "c":
            case "h":
                return "c";
            case "cpp":
            case "cc":
            case "cxx":
            case "hpp":
                return "cpp";
            case "cs":
                return "csharp";
            default:
                return "java"; // Default to Java if unknown
        }
    }
}
