    private void setupDialog() {
        dialog = new Dialog<>();
        dialog.setTitle("PDF Settings");
        dialog.setHeaderText("Configure PDF Output Settings");
        dialog.initOwner(ownerStage);

        // Set the button types
        ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
        dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);

        // Create the settings form
        GridPane grid = new GridPane();
        grid.setHgap(10);
        grid.setVgap(10);
        grid.setPadding(new Insets(20, 150, 10, 10));

        // Font Family
        fontFamilyComboBox = new ComboBox<>();
        fontFamilyComboBox.getItems().addAll(
                "Courier New", "Consolas", "DejaVu Sans Mono", "Liberation Mono", "Monospace"
        );
        fontFamilyComboBox.setValue("Courier New");
        grid.add(new Label("Font Family:"), 0, 0);
        grid.add(fontFamilyComboBox, 1, 0);

        // Font Size
        fontSizeSpinner = new Spinner<>(8, 24, 10);
        fontSizeSpinner.setEditable(true);
        grid.add(new Label("Font Size:"), 0, 1);
        grid.add(fontSizeSpinner, 1, 1);

        // Tab Size
        tabSizeSpinner = new Spinner<>(2, 8, 4);
        tabSizeSpinner.setEditable(true);
        grid.add(new Label("Tab Size:"), 0, 2);
        grid.add(tabSizeSpinner, 1, 2);

        // Preserve Tabs
        preserveTabsCheckBox = new CheckBox("Preserve Tabs");
        preserveTabsCheckBox.setSelected(false);
        grid.add(preserveTabsCheckBox, 0, 3, 2, 1);

        // Line Numbers
        lineNumbersCheckBox = new CheckBox("Show Line Numbers");
        lineNumbersCheckBox.setSelected(true);
        grid.add(lineNumbersCheckBox, 0, 4, 2, 1);

        // Wrap Lines
        wrapLinesCheckBox = new CheckBox("Wrap Long Lines");
        wrapLinesCheckBox.setSelected(true);
        grid.add(wrapLinesCheckBox, 0, 5, 2, 1);

        // Theme 
