#!/bin/bash

# Exit on error
set -e

echo "=== Building Code to PDF Converter Distribution Package ==="

# Get the project directory
PROJECT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )"
cd "$PROJECT_DIR"

# Check if the critical files exist
if [ ! -f "src/main/java/org/example/PdfSettingsDialog.java" ]; then
    echo "Error: PdfSettingsDialog.java is missing or has syntax errors."
    echo "Please make sure all required Java files are properly created."
    exit 1
fi

if [ ! -f "src/main/java/org/example/CodeFormattingPreserver.java" ]; then
    echo "Error: CodeFormattingPreserver.java is missing or has syntax errors."
    echo "Please make sure all required Java files are properly created."
    exit 1
fi

# Ensure Java is installed
if ! command -v java &> /dev/null; then
    echo "Error: Java is not installed or not in PATH"
    echo "Please install Java 17 or newer and try again"
    exit 1
fi

# Check Java version
JAVA_VERSION=$(java -version 2>&1 | head -1 | cut -d'"' -f2 | sed 's/^1\.//' | cut -d'.' -f1)
if [ "$JAVA_VERSION" -lt 17 ]; then
    echo "Error: Java 17 or newer is required (found Java $JAVA_VERSION)"
    echo "Please install Java 17 or newer and try again"
    exit 1
fi

echo "Using Java version: $(java -version 2>&1 | head -1)"

# Check Maven installation
if ! command -v mvn &> /dev/null; then
    echo "Error: Maven is not installed or not in PATH"
    echo "Please install Maven and try again"
    exit 1
fi

# Clean project first to ensure fresh build
echo "Cleaning project..."
mvn clean

# Compile project first to catch any compilation errors early
echo "Compiling project..."
if ! mvn compile; then
    echo "Error: Compilation failed. Please fix the issues and try again."
    exit 1
fi

# Build the project with Maven
echo "Building project with Maven..."
if ! mvn package; then
    echo "Error: Maven build failed"
    exit 1
fi

# Create distribution directory
DIST_DIR="$PROJECT_DIR/dist"
rm -rf "$DIST_DIR"
mkdir -p "$DIST_DIR"

# Copy JAR file
echo "Creating distribution package..."
if [ -f "$PROJECT_DIR/target/codepdf-1.0.jar" ]; then
    cp "$PROJECT_DIR/target/codepdf-1.0.jar" "$DIST_DIR/"
else
    echo "Error: JAR file not found at $PROJECT_DIR/target/codepdf-1.0.jar"
    echo "Build might have failed. Check maven output for details."
    exit 1
fi

# Create launcher scripts
cat > "$DIST_DIR/run.sh" << 'EOF'
#!/bin/bash
# Launcher script for Code to PDF Converter

# Find the script directory
SCRIPT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )"
cd "$SCRIPT_DIR"

# Run the application
java -jar codepdf-1.0.jar "$@"
EOF
chmod +x "$DIST_DIR/run.sh"

# Windows batch file
cat > "$DIST_DIR/run.bat" << 'EOF'
@echo off
REM Launcher script for Code to PDF Converter

REM Change to the script directory
cd /d "%~dp0"

REM Run the application
java -jar codepdf-1.0.jar %*
EOF

# Create a launcher in project root for convenience
cat > "$PROJECT_DIR/launcher.sh" << 'EOF'
#!/bin/bash

# Find the script directory
SCRIPT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )"

# Check if the JAR exists in target directory
if [ -f "$SCRIPT_DIR/target/codepdf-1.0.jar" ]; then
    # Run the application
    java -jar "$SCRIPT_DIR/target/codepdf-1.0.jar" "$@"
elif [ -f "$SCRIPT_DIR/dist/codepdf-1.0.jar" ]; then
    # Try dist directory if target doesn't have it
    java -jar "$SCRIPT_DIR/dist/codepdf-1.0.jar" "$@"
else
    echo "Error: Unable to find JAR file. Please build the project first."
    echo "Run: ./build.sh"
    exit 1
fi
EOF
chmod +x "$PROJECT_DIR/launcher.sh"

# Create a README file in the distribution
cat > "$DIST_DIR/README.txt" << 'EOF'
Code to PDF Converter
=====================

This tool converts source code files to beautifully formatted PDFs with:
- Line numbers
- Syntax highlighting
- Smart line wrapping for long lines
- Support for multiple programming languages
- Special handling for C/C++ declarations

How to use:
-----------
1. On Windows: Double-click run.bat
