```java
package org.example;
package org.example;
package org.example;

import javafx.geometry.Insets;
import javafx.scene.control.*;
import javafx.scene.layout.GridPane;
import javafx.scene.layout.VBox;
import javafx.stage.Stage;
import javafx.util.Pair;

/**
 * Dialog for configuring PDF generation settings, especially related to code formatting.
 */
public class PdfSettingsDialog {
    private final Stage ownerStage;
    private Dialog<PdfSettings> dialog;

    // UI controls
    private ComboBox<String> fontFamilyComboBox;
    private Spinner<Integer> fontSizeSpinner;
    private Spinner<Integer> tabSizeSpinner;
    private CheckBox preserveTabsCheckBox;
    private CheckBox lineNumbersCheckBox;
    private CheckBox wrapLinesCheckBox;
    private ComboBox<String> themeComboBox;
    private CheckBox landscapeCheckBox;

    public PdfSettingsDialog(Stage ownerStage) {
        this.ownerStage = ownerStage;
        setupDialog();
    }

    private void setupDialog() {
        dialog = new Dialog<>();
        dialog.setTitle("PDF Settings");
        dialog.setHeaderText("Configure PDF Output Settings");
        dialog.initOwner(ownerStage);

        // Set the button types
        ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
        dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);

        // Create the settings form
        GridPane grid = new GridPane();
        grid.setHgap(10);
        grid.setVgap(10);
        grid.setPadding(new Insets(20, 150, 10, 10));

        // Font Family
        fontFamilyComboBox = new ComboBox<>();
        fontFamilyComboBox.getItems().addAll(
                "Courier New", "Consolas", "DejaVu Sans Mono", "Liberation Mono", "Monospace"
        );
        fontFamilyComboBox.setValue("Courier New");
        grid.add(new Label("Font Family:"), 0, 0);
        grid.add(fontFamilyComboBox, 1, 0);

        // Font Size
        fontSizeSpinner = new Spinner<>(8, 24, 10);
        fontSizeSpinner.setEditable(true);
        grid.add(new Label("Font Size:"), 0, 1);
        grid.add(fontSizeSpinner, 1, 1);

        // Tab Size
        tabSizeSpinner = new Spinner<>(2, 8, 4);
        tabSizeSpinner.setEditable(true);
        grid.add(new Label("Tab Size:"), 0, 2);
        grid.add(tabSizeSpinner, 1, 2);

        // Preserve Tabs
        preserveTabsCheckBox = new CheckBox("Preserve Tabs");
        preserveTabsCheckBox.setSelected(false);
        grid.add(preserveTabsCheckBox, 0, 3, 2, 1);

        // Line Numbers
        lineNumbersCheckBox = new CheckBox("Show Line Numbers");
        lineNumbersCheckBox.setSelected(true);
        grid.add(lineNumbersCheckBox, 0, 4, 2, 1);

        // Wrap Lines
        wrapLinesCheckBox = new CheckBox("Wrap Long Lines");
        wrapLinesCheckBox.setSelected(true);
        grid.add(wrapLinesCheckBox, 0, 5, 2, 1);

        // Theme 
        themeComboBox = new ComboBox<>();
        themeComboBox.getItems().addAll("Light", "Dark", "Solarized Light", "Solarized Dark");
        themeComboBox.setValue("Light");
        grid.add(new Label("Theme:"), 0, 6);
        grid.add(themeComboBox, 1, 6);

        // Page Orientation
        landscapeCheckBox = new CheckBox("Landscape Orientation");
        landscapeCheckBox.setSelected(false);
        grid.add(landscapeCheckBox, 0, 7, 2, 1);

        dialog.getDialogPane().setContent(grid);

        // Convert the result to PdfSettings when the save button is clicked
        dialog.setResultConverter(dialogButton -> {
            if (dialogButton == saveButtonType) {
                PdfSettings settings = new PdfSettings();
                settings.setFontFamily(fontFamilyComboBox.getValue());
                settings.setFontSize(fontSizeSpinner.getValue());
                settings.setTabSize(tabSizeSpinner.getValue());
                settings.setPreserveTabs(preserveTabsCheckBox.isSelected());
                settings.setShowLineNumbers(lineNumbersCheckBox.isSelected());
                settings.setWrapLines(wrapLinesCheckBox.isSelected());
                settings.setTheme(themeComboBox.getValue());
                settings.setLandscape(landscapeCheckBox.isSelected());
                return settings;
            }
            return null;
        });
    }

    /**
     * Shows the settings dialog and returns the user's selection
     * @return PdfSettings object or null if dialog was canceled
     */
    public PdfSettings showAndGetSettings() {
        return dialog.showAndWait().orElse(null);
    }
}

