import javafx.geometry.Insets;
import javafx.scene.control.*;
import javafx.scene.layout.GridPane;
import javafx.scene.layout.VBox;
import javafx.stage.Stage;
import javafx.util.Pair;

/**
 * Dialog for configuring PDF generation settings, especially related to code formatting.
 */
public class PdfSettingsDialog extends Dialog<PdfSettings> {

    public PdfSettingsDialog(Stage owner) {
        setTitle("PDF Generation Settings");
        setHeaderText("Configure how your code will be formatted in the PDF");
        initOwner(owner);

        // Set the button types
        ButtonType saveButtonType = new ButtonType("Save Settings", ButtonBar.ButtonData.OK_DONE);
        getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);

        // Create the form fields
        GridPane grid = new GridPane();
        grid.setHgap(10);
        grid.setVgap(10);
        grid.setPadding(new Insets(20, 150, 10, 10));

        // Font settings
        ComboBox<String> fontComboBox = new ComboBox<>();
        fontComboBox.getItems().addAll(
                "Courier New", "Consolas", "DejaVu Sans Mono", "Liberation Mono", "Monaco", "Menlo"
        );
        fontComboBox.setValue("Courier New");

        Spinner<Integer> fontSizeSpinner = new Spinner<>(8, 16, 11);
        fontSizeSpinner.setEditable(true);

        // Tab settings
        CheckBox preserveTabsCheckBox = new CheckBox("Preserve tabs in output");
        preserveTabsCheckBox.setSelected(true);

        Spinner<Integer> tabSizeSpinner = new Spinner<>(2, 8, 4);
        tabSizeSpinner.setEditable(true);
        preserveTabsCheckBox.selectedProperty().addListener((obs, oldVal, newVal) -> {
            tabSizeSpinner.setDisable(newVal); // Disable tab size if preserving tabs
        });

        // Line number settings
        CheckBox showLineNumbersCheckBox = new CheckBox("Show line numbers");
        showLineNumbersCheckBox.setSelected(true);

        // Line wrapping settings
        CheckBox wrapLinesCheckBox = new CheckBox("Wrap long lines");
        wrapLinesCheckBox.setSelected(false);

        // Theme settings
        ComboBox<String> themeComboBox = new ComboBox<>();
        themeComboBox.getItems().addAll(
                "Light", "Dark", "Solarized Light", "Solarized Dark"
        );
        themeComboBox.setValue("Light");

        // Page orientation
        ToggleGroup orientationGroup = new ToggleGroup();
        RadioButton portraitRadio = new RadioButton("Portrait");
        RadioButton landscapeRadio = new RadioButton("Landscape");
        portraitRadio.setToggleGroup(orientationGroup);
        landscapeRadio.setToggleGroup(orientationGroup);
        landscapeRadio.setSelected(true);

        // Add rows to grid
        int row = 0;
        grid.add(new Label("Font Family:"), 0, row);
        grid.add(fontComboBox, 1, row++);

        grid.add(new Label("Font Size:"), 0, row);
        grid.add(fontSizeSpinner, 1, row++);

        grid.add(preserveTabsCheckBox, 0, row++, 2, 1);

        grid.add(new Label("Tab Size:"), 0, row);
        grid.add(tabSizeSpinner, 1, row++);
        tabSizeSpinner.setDisable(preserveTabsCheckBox.isSelected());

        grid.add(showLineNumbersCheckBox, 0, row++, 2, 1);
        grid.add(wrapLinesCheckBox, 0, row++, 2, 1);

        grid.add(new Label("Theme:"), 0, row);
        grid.add(themeComboBox, 1, row++);

        // Orientation
        VBox orientationBox = new VBox(10, new Label("Page Orientation:"), portraitRadio, landscapeRadio);
        grid.add(orientationBox, 0, row++, 2, 1);

        getDialogPane().setContent(grid);

        // Convert the result to settings when the save button is clicked
        setResultConverter(dialogButton -> {
            if (dialogButton == saveButtonType) {
                PdfSettings settings = new PdfSettings();
                settings.setFontFamily(fontComboBox.getValue());
                settings.setFontSize(fontSizeSpinner.getValue());
                settings.setPreserveTabs(preserveTabsCheckBox.isSelected());
                settings.setTabSize(tabSizeSpinner.getValue());
                settings.setShowLineNumbers(showLineNumbersCheckBox.isSelected());
                settings.setWrapLines(wrapLinesCheckBox.isSelected());
                settings.setTheme(themeComboBox.getValue());
                settings.setLandscape(landscapeRadio.isSelected());
                return settings;
            }
            return null;
        });
    }

    /**
     * Shows the dialog and returns the configured settings
     * @return The settings if OK was clicked, otherwise default settings
     */
    public PdfSettings showAndGetSettings() {
        return showAndWait().orElse(new PdfSettings());
    }
}

