        themeComboBox.setValue("Light");
        grid.add(new Label("Theme:"), 0, 6);
        grid.add(themeComboBox, 1, 6);

        // Page Orientation
        landscapeCheckBox = new CheckBox("Landscape Orientation");
        landscapeCheckBox.setSelected(false);
        grid.add(landscapeCheckBox, 0, 7, 2, 1);

        dialog.getDialogPane().setContent(grid);

        // Convert the result to PdfSettings when the save button is clicked
