        grid.add(wrapLinesCheckBox, 0, 4, 2, 1);

        grid.add(new Label("Theme:"), 0, 5);
        grid.add(themeComboBox, 1, 5);

        grid.add(new Label("Page Orientation:"), 0, 6);
        VBox orientationBox = new VBox(5, portraitRadio, landscapeRadio);
        grid.add(orientationBox, 1, 6);

