<llm-patch path="launcher.sh" matcher

/**
 * Utility class for preserving exact code formatting when converting to PDF.
 * Handles tabs, spaces, and all whitespace exactly as they appear in the source file.
 */
public class CodeFormattingPreserver {

    /**
     * Reads a source code file and preserves its exact formatting.
     * 
     * @param filePath Path to the source code file
     * @return List of lines with exact formatting preserved
     * @throws IOException If there's an error reading the file
     */
    public static List<String> readCodeWithExactFormatting(String filePath) throws IOException {
        return Files.readAllLines(Paths.get(filePath));
    }

    /**
     * Converts tabs to a fixed number of spaces for consistent rendering.
     * 
     * @param input The line of code with potential tabs
     * @param tabSize Number of spaces per tab
     * @return Line with tabs converted to spaces
     */
    private static String convertTabsToSpaces(String input, int tabSize) {
        StringBuilder sb = new StringBuilder();
        int column = 0;

        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);

            if (c == '\t') {
                int spacesToAdd = tabSize - (column % tabSize);
                for (int j = 0; j < spacesToAdd; j++) {
                    sb.append(' ');
                }
                column += spacesToAdd;
            } else {
                sb.append(c);
                column++;
            }
        }

        return sb.toString();
    }

    /**
     * Creates HTML with exact formatting preservation
     * @param codeLines The code lines to format
     * @param preserveTabs Whether to preserve tabs or convert to spaces
     * @param tabSize Tab size to use if converting tabs to spaces
     * @return Formatted HTML string
     */
    public static String createFormattingPreservedHtml(List<String> codeLines, boolean preserveTabs, int tabSize) {
        StringBuilder sb = new StringBuilder();

        sb.append("<pre>");

        for (int i = 0; i < codeLines.size(); i++) {
            String line = codeLines.get(i);

            // Escape HTML special characters
            line = line.replace("&", "&amp;")
                       .replace("<", "&lt;")
                       .replace(">", "&gt;");

            // Handle tabs based on settings
            if (!preserveTabs) {
                line = convertTabsToSpaces(line, tabSize);
            }

            sb.append("<code>");

            // Add line number span before content
            sb.append("<span class=\"line-number\">")
              .append(String.format("%4d", i + 1))
              .append("</span>");

            // Check if this is an error message and format it specially
            if (line.contains("Error processing file:") || line.contains("Error converting file:") || line.contains("Exception:")) {
                sb.append("<span class=\"error-message\">");

                // Format error messages to ensure proper wrapping with indentation
                if (line.contains(" - ")) {
                    String[] parts = line.split(" - ", 2);
                    sb.append(parts[0]).append(" - ");
                    sb.append("<span class=\"wrapped-line\">").append(parts[1]).append("</span>");
                } else {
                    sb.append(line);
                }

                sb.append("</span>");
            } else if (isCppDeclaration(line)) {
                // Handle C/C++ declarations with special formatting
                sb.append(formatCppDeclaration(line));
            } else {
                // Add the actual code content
                sb.append("<span class=\"code-line\">")
                  .append(line)
                  .append("</span>");
            }

            sb.append("</code>\n");
        }

        sb.append("</pre>");
        return sb.toString();
    }

    /**
     * Returns CSS styles for preserving code formatting in PDFs.
     * 
     * @return CSS styles as a string
     */
    public static String getFormattingPreservationCss() {
        return "pre, code { font-family: 'Courier New', monospace !important; }\n" +
               "pre { white-space: pre !important; overflow-x: visible !important; }\n" +
               "code { tab-size: 4; -moz-tab-size: 4; }\n" +
               ".line-number { color: #888888; user-select: none; }\n" +
               ".code-container { width: 100%; overflow-x: auto; }\n" +
               ".cpp-declaration { display: table; width: 100%; }\n" +
               ".cpp-continuation { display: block; padding-left: 2em; text-indent: 0; }\n" +
               ".wrapped-line { display: block; padding-left: 2em; text-indent: 0; }\n";
    }

    /**
     * Detects if the line is a C/C++ declaration or definition that might need special wrapping
     * @param line The line to check
     * @return true if this is a C/C++ declaration that needs special handling
     */
    private static boolean isCppDeclaration(String line) {
        // Check for preprocessor directives
        if (line.trim().startsWith("#")) {
            return true;
        }

        // Check for function declarations/definitions
        if (line.contains("(") && line.contains(")") && 
            !line.contains(";") && !line.contains("{")) {
            return true;
        }

        // Check for GLib/GTK macros common in C code
        if (line.contains("G_BEGIN") || line.contains("G_END") || 
            line.contains("G_DECLARE") || line.contains("_get_type()")) {
            return true;
        }

        return false;
    }

    /**
     * Format a C/C++ declaration to ensure proper line wrapping
     * @param line The line to format
     * @return Formatted line with proper wrapping markup
     */
    private static String formatCppDeclaration(String line) {
        StringBuilder formatted = new StringBuilder();

        // If the line is already short, return it as is
        if (line.length() < 80) {
            return line;
        }

        // Find logical break points
        String[] breakPoints = {",", " ", "(", ")", "{", "}", "<", ">"};
        int lastBreakIndex = 0;

        formatted.append("<span class=\"cpp-declaration\">");

        for (int i = 0; i < line.length(); i++) {
            // Check if we should break here
            for (String breakPoint : breakPoints) {
                if (i > 50 && i < line.length() && line.substring(i).startsWith(breakPoint)) {
                    // Add current segment
                    formatted.append(line.substring(lastBreakIndex, i));
                    formatted.append("</span><span class=\"cpp-continuation\">");
                    lastBreakIndex = i;
                    i = lastBreakIndex; // Reset counter to start with the break point
                    break;
                }
            }
        }

        // Add the remaining part
        formatted.append(line.substring(lastBreakIndex));
        formatted.append("</span>");

        return formatted.toString();
    }
}

