echo "=== Building Code to PDF Converter Distribution Package ==="

# Get the project directory
PROJECT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )"
cd "$PROJECT_DIR"

# Pre-flight syntax check for critical Java files
echo "Checking for syntax errors in Java files..."

# Function to check for common Java syntax errors
check_java_syntax() {
    local file="$1"

    if [ ! -f "$file" ]; then
        echo "Error: File $file does not exist."
        return 1
    fi

    # Check for package declaration
    if ! grep -q "^package " "$file"; then
        echo "Warning: No package declaration in $file"
    fi

    # Check for multiple package declarations (common error)
    if [ $(grep -c "^package " "$file") -gt 1 ]; then
        echo "Error: Multiple package declarations in $file"
        echo "Please remove duplicate package statements."
        return 1
    fi

    # Check for imports of classes that are used
    if grep -q "Files\\." "$file" && ! grep -q "import.*Files;" "$file"; then
        echo "Error: Using Files without import in $file"
        echo "Add: import java.nio.file.Files;"
        return 1
    fi

    if grep -q "Paths\\." "$file" && ! grep -q "import.*Paths;" "$file"; then
        echo "Error: Using Paths without import in $file"
        echo "Add: import java.nio.file.Paths;"
        return 1
    fi

    if grep -q "IOException" "$file" && ! grep -q "import.*IOException;" "$file"; then
        echo "Error: Using IOException without import in $file"
        echo "Add: import java.io.IOException;"
        return 1
    fi

    if grep -q "List<" "$file" && ! grep -q "import.*List;" "$file"; then
        echo "Error: Using List without import in $file"
        echo "Add: import java.util.List;"
        return 1
    fi

    # Check for balanced braces
    local open_braces=$(grep -o "{" "$file" | wc -l)
    local close_braces=$(grep -o "}" "$file" | wc -l)

    if [ "$open_braces" != "$close_braces" ]; then
        echo "Error: Unbalanced braces in $file"
        echo "Open braces: $open_braces, Close braces: $close_braces"
        return 1
    fi

    # Check for multiple class definitions with the same name
    local class_name=$(basename "$file" .java)
    local class_count=$(grep -c "class $class_name" "$file")

    if [ "$class_count" -gt 1 ]; then
        echo "Warning: Multiple class $class_name definitions in $file"
    fi

    return 0
}

# Ensure src and target directories exist
mkdir -p src/main/java/org/example
mkdir -p target

# Find all Java files in the project
echo "Scanning for Java files..."
JAVA_FILES=$(find src -name "*.java")

if [ -z "$JAVA_FILES" ]; then
    echo "Warning: No Java files found in the project"
fi

# Check syntax of all Java files
for file in $JAVA_FILES; do
    echo "Checking $file..."
    if ! check_java_syntax "$file"; then
        echo "Syntax error detected in $file. Please fix it before building."
        exit 1
    fi
done

# Check if Maven is installed
if ! command -v mvn &> /dev/null; then
    echo "Error: Maven is not installed or not in PATH"
    echo "Please install Maven and try again"
    exit 1
fi

# Check Maven configuration
if [ ! -f "pom.xml" ]; then
    echo "Error: pom.xml not found. Maven configuration is required."
    exit 1
fi

# Check if the critical files exist
if [ ! -f "src/main/java/org/example/PdfSettingsDialog.java" ]; then
    echo "Error: PdfSettingsDialog.java is missing."
    echo "Please make sure all required Java files are properly created."
    exit 1
fi

if [ ! -f "src/main/java/org/example/CodeFormattingPreserver.java" ]; then
    echo "Error: CodeFormattingPreserver.java is missing."
    echo "Please make sure all required Java files are properly created."
    exit 1
fi

echo "All syntax checks passed. Proceeding with build..."
